import produce from 'immer'
const InvitesInitialState={
    invite:{},
    invaites:[],
    ShopingBasket:[]
}
export const InvitesReducer=produce((state,action)=>{
    switch(action.type)
    {
        case "MAKE_PURCHASE":state.invaites.push(action.payload);break;
    }
},InvitesInitialState)
