import React from "react";
import { useDispatch } from 'react-redux';
import { MDBContainer, MDBRow, MDBCol, MDBBtn, MDBInput } from 'mdbreact';
import { getCustomerFromserverById } from '../Redux/Customer/CustomersThunk';
import FormPage from './SignUp'
import  {isMenneger}  from "../Redux/Customer/CustomerAction";

export default function Menneger() {
    const dispatch = useDispatch();
    // let [manage,setManage] = useSelector()=useState((store) => { return store.users.isMenneger });
    const IsMember = async (dispatch) => {
      const name = document.getElementById("id").value;
      const pass = document.getElementById("pass").value;
      const Menegerpass = document.getElementById("Menegerpass").value;
      
      const customer = await getUserFromserverBynameAndPass(dispatch, id, pass);
      if (customer && Menegerpass == "1111") {
        dispatch(isMenneger(1));  
        alert("ברוך הבא " +id);
        alert("הינך על מצב מנהל אנא ודא את שינוי הגדרת המשתמש לפני יציאתך...")
        return customer;
      }
      else {
        alert("אחד או יותר מהנתונים שגוי...");
        return;
      } 
    }
    return (
        <MDBContainer>
        <MDBRow>
          <MDBCol md="6">
            <form>
              <p className="h4 text-center mb-4">Sign in</p>
              <label htmlFor="defaultFormLoginEmailEx" className="grey-text">
                Your id
              </label>
              <input type="text" id="id" className="form-control" validate error="wrong" />
              <br />
              <label htmlFor="defaultFormLoginPasswordEx" className="grey-text">
                Your password
              </label>
              <input type="password" id="pass" className="form-control" validate id="pass" />

              <label htmlFor="defaultFormLoginPasswordEx" className="grey-text">
                Your Menegerpassword
              </label>
              <input type="password" id="Menegerpass" className="form-control" validate id="pass" />
              <div className="text-center mt-4">
                <MDBBtn color="indigo" onClick={()=>IsMember(dispatch)}>התחבר</MDBBtn>
              </div>
            </form>
          </MDBCol>
        </MDBRow>
      </MDBContainer>
      );
      };
