import React from 'react';
import {
  MDBCol,
  MDBContainer,
  MDBRow,
  MDBCard,
  MDBCardText,
  MDBCardBody,
  MDBCardImage,
  MDBBtn,
  MDBBreadcrumb,
  MDBBreadcrumbItem,
  MDBProgress,
  MDBProgressBar,
  MDBIcon,
  MDBListGroup,
  MDBListGroupItem,
  MDBTable,
  MDBTableHead,
  MDBTableBody,
  MDBFile 
} from 'mdb-react-ui-kit';
import { useHistory } from 'react-router';
import { getUserById } from '../Redux/Users/UsersThunk';
import { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getVaccinationById } from '../Redux/Vaccinations/VaccinationsThunk';
import {ifSick} from '../Redux/Users/UsersThunk'

export default function ProfilePage(props) {
    const history=useHistory();
    let User = useSelector((store) => {return store.Users.user }); 
    const dispatch = useDispatch();
    const [user,setUser]=useState([]);
    const [user2,setUser2]=useState({});
    const [sick,setSick]=useState();
    const [myFile, setMyfile] = useState();
    const [file, setFile] = useState(null);
    const [error, setError] = useState(null);
    
    useEffect(async () => {
      let ifsick=0
      let U
      let U2
        let id=props.match.params.id
        if(id){
           U = await getVaccinationById(dispatch,id);
           U2 = await getUserById(dispatch,id);
           ifsick = await ifSick(id)
         
        }
        else{
         U = await getVaccinationById(dispatch,User.idUser);
         U2 = await getUserById(dispatch,User.idUser);
         ifsick = await ifSick(User.idUser)
         
         
        }
        setSick(ifsick)
        if(U2){
         setUser(U)
         setUser2(U2)
        }
        else{
            alert("אינך מחובר למערכת אנא התחבר או הרשם תחילה")
        }
        }, [])

        const handleFileChange = (event) => {
          const selectedFile = event.target.files[0];
      
          if (selectedFile && isImageFile(selectedFile)) {
            setFile(selectedFile);
            setError(null);
          } else {
            setFile(null);
            setError("Please select a valid image file (JPEG, PNG, or GIF)");
          }
        };
      
        const handleUpload = async () => {
          if (file) {
            const formData = new FormData();
            formData.append("image", file);
      
            try {
              const response = await fetch("https://localhost:44315/api/Users/Upload", {
                method: "POST",
                body: formData,
              });
      
              if (response.ok) {

                try{
                 
                  const PicturePromise1 = axios.put("https://localhost:44315/api/Users/UpdateImage/" +U.userId+ "/" + file.name);
                }
                  
              catch{
                alert("adhtv")
              }
                  
                  alert("תמונה הועלתה בהצלחה");
    
                 
                  
                 
            
               

              } else {
                throw new Error(await response.text());
              }
            } catch (ex) {
              alert(`Error: ${ex.message}`);
            }
          } else {
            setError("אנא בחר תמונה");
          }
        };
      
        const isImageFile = (file) => {
          const acceptedImageTypes = ["image/jpeg", "image/png", "image/gif"];
          return file && acceptedImageTypes.includes(file.type);
        }

  return (
    <section style={{ backgroundColor: '#eee' ,padding:"10px 0px", margin:"0 auto" ,borderRadius:"8px"}} className="col-11">
      <MDBContainer className="p-5">
        
        <MDBRow>
          <MDBCol lg="4">
            <MDBCard className="mb-4">
              <MDBCardBody className="text-center">
                <MDBCardImage
                  src="https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-chat/ava3.webp"
                  alt="avatar"
                  className="rounded-circle"
                  style={{ width: '150px' }}
                  fluid />
                <p className="text-muted mb-1">{user2.idUser}</p>
                <p className="text-muted mb-1">{user2.privateName} {user2.familyName}</p>
                <p className="text-muted mb-1">{user2.cityAdress} {user2.streetAdress} {user2.numberAdress}</p>
                <p className="text-muted mb-1">תאריך לידה: {user2.dateOfBirth}</p>
                <p className="text-muted mb-1">טלפון: {user2.phone}</p>
                <p className="text-muted mb-1">פלאפון: {user2.mobilePhone}</p>
               
                <div className="d-flex justify-content-center mb-2">
                <MDBBtn outline className="ms-1"><MDBFile onChange={handleFileChange} />העלאת תמונה</MDBBtn>
                {error && <div style={{ color: "red" }}>{error}</div>}
                <MDBBtn   outline className="ms-1" onClick={handleUpload}>שמור תמונה</MDBBtn>
              
                
                </div>
              </MDBCardBody>
            </MDBCard>
          </MDBCol>

          <MDBCol lg="8">
            <MDBCard className="mb-4">
               <MDBCardBody>
               <MDBTable>
      <MDBTableHead>
        <tr>
          <th scope='col'>מספר חיסון</th>
          <th scope='col'>יצרן</th>
          <th scope='col'>תאריך</th>
        </tr>
      </MDBTableHead>
      {user === undefined ? <h3>wait...</h3> :
       user.map((f, i) =>
      <MDBTableBody>
     
        <tr>
          <th scope='row'>{f.numVaccination}</th>
          <td>{f.manufacturer}</td>
          <td className='date_v'>{f.dateOfVaccination}</td>
        </tr>
      </MDBTableBody>
      )}

    </MDBTable>
    <MDBBtn   outline className="ms-1" onClick={()=>{props.history.push("/SignUp1")}}>הוסף חיסון </MDBBtn>

              </MDBCardBody>  
             </MDBCard> 
             <MDBRow>
             <MDBCol md="6">
                <MDBCard className="mb-4 mb-md-0">
                  <MDBCardBody>
                    <p>האם חליתי בעבר?</p>
                  {(sick==1)&&
                    <p>כן</p>
                  }
                  {(sick==2)&&
                    <p>כן</p>
                  }
                   {(sick==0)&&
                    <p>לא</p>
                  }
                  </MDBCardBody>
                </MDBCard>
              </MDBCol>
              <MDBCol md="6">
                <MDBCard className="mb-4 mb-md-0">
                  <MDBCardBody>
                    <p>האם החלמתי?</p>
                    {(sick==1)&&
                    <p>לא</p>
                  }
                   {(sick==2)&&
                    <p>כן</p>
                  }
                   {(sick==0)&&
                    <p>לא</p>
                  }
                  </MDBCardBody>
                </MDBCard>
              </MDBCol>
              
          </MDBRow>
          {/* <MDBCard>
          <MDBBtn className="mb-4" onClick={()=>{props.history.push("/ProfilePage")}} >הוספת חיסון</MDBBtn>
          </MDBCard> */}

          </MDBCol>
        </MDBRow>
      </MDBContainer>
    </section>
  );
}
