import React, { Component } from "react";
import { MDBRow, MDBCard, MDBCardBody, MDBTooltip, MDBTable, MDBTableBody, MDBTableHead, MDBInput, MDBBtn } from "mdbreact";
import { useDispatch,useSelector } from "react-redux";
import { DoInviteByBascet } from '../Redux/Invity/InvityThunk'
import {updateProductBasket} from '../Redux/Customer/CustomersThunk'
import { DeletProductFromBasket } from '../Redux/Customer/CustomerAction'


export default function eCommercePage() {

  const dispatch=useDispatch();
  const Basket=useSelector((s)=>{return s.customers.basket});

 alert("שים לב שהמוצרים שהוספת לסל ימחקו ממנו בעת יציאתך מהחשבון שלך...")

 console.log("Basket")
 console.log(Basket)
return<>

<MDBRow className="my-2" center>
      <MDBCard className="w-100">
        <MDBCardBody>
          <MDBTable className="product-table">
            <MDBTableHead className="font-weight-bold" color="mdb-color lighten-5" >
            <td><img></img></td>
    <td> <strong>שם תמונה</strong> </td>
    <td><strong>מחיר ליחידה</strong></td>
    <td><strong>כמות</strong></td>
    <td><strong>מחיר סופי</strong></td>
  <td></td>
  </MDBTableHead>
            <MDBTableBody> 
            {
          
          Basket.map((item)=>
    
      <tr>
         <td><img src={item.imageProduct} alt="" className="img-fluid z-depth-0" /></td>
        <td><h5 className="mt-3"><strong>{item.nameProduct}</strong></h5></td>
        <td> <p className="text-muted">{item.priceProduct}</p></td>
        <td>{item.priceProduct}</td>

          <td><MDBInput onChange={()=>updateProductFromBasket1(dispatch,item,Basket)} type="number" value={item.amount} className="form-control"  id="amount" style={{ width: "100px" }} /></td>

          <td> <strong>{item.priceProduct*item.amount}</strong></td>
        <td>
          <MDBTooltip placement="top">
              <MDBBtn color="primary" size="sm" onClick={()=> deletProductFromBasket1(dispatch,item)}>
                  X
              </MDBBtn>
              <div>הסר פריט זה מהסל</div>
          </MDBTooltip></td> 
        </tr>)}


   </MDBTableBody>
   <button onClick={()=>DoInviteByB(dispatch)}>בצע קנייה</button>
          </MDBTable>
        </MDBCardBody>
      </MDBCard>
    </MDBRow> 

  </>
}  

   const updateProductFromBasket1=async(dispatch, p,Basket)=>{
     
    const pInB={
      idProduct:p.idProduct,
      nameProduct:p.nameProduct,
      amount:document.getElementById("amount").value,
      price:p.price,
      finalPrice:p.price*amount
     }
      await updateProductBasket(dispatch,pInB,Basket);
     }
    
     const deletProductFromBasket1=(dispatch,x)=>{
        alert(x.nameProduct);
        debugger
            dispatch(DeletProductFromBasket(x));
          }
         
         
          const DoInviteByB=async(dispatch,Basket)=>{
           
                await DoInviteByBascet(dispatch,Basket);
              }
