import { useState } from 'react';
import { useSelector } from 'react-redux'; 

export default function ChartsPage() {


  const [dataLine,setDataLine]=useState(["ינואר", "פברואר", "מרץ", "אפריל", "מאי", "יוני", "יולי","אוגוסט","ספטמבר","אוקטובר","נובמבר","דצמבר"]);

const [positive,setPositive]=useState(
  
[65, 59, 80, 81, 56, 55, 40, 70, 75, 20, 30, 42, 89,80]
);
 
const [negetive,setNegetive]=useState(
    [28, 48, 40, 19, 86, 27, 90, 20, 32, 56,45 , 80]
    );
    const [unsup,setUnsup]=useState(
[50, 60, 80, 45, 65, 78, 36, 98, 78, 90,89 , 40]);
  


    return (
      <MDBContainer>
        <h3 className="mt-5">תרשים שנתי</h3>

        <Line data={
          {positive ,
          negetive,
          unsup}
        } options={{ responsive: true }} />

        <Line options={{ responsive: true }} />
        <Line options={{ responsive: true }} />
        <Line options={{ responsive: true }} /> 
        
      </MDBContainer>
    );
  }

const styleP={
  
  label: "ביקורות שליליות",
  fill: true,
  lineTension: 0.3,
  backgroundColor: "rgba(184, 185, 210, .3)",
  borderColor: "rgb(35, 26, 136)",
  borderCapStyle: "butt",
  borderDash: [],
  borderDashOffset: 0.0,
  borderJoinStyle: "miter",
  pointBorderColor: "rgb(35, 26, 136)",
  pointBackgroundColor: "rgb(255, 255, 255)",
