export function GetAllCustomersOfCompanies(company) {
    return { type: "GET_ALL_CUSTOMERS_OF_COMPANIES",paylod:company }
}
export function GetAllCustomersByCompany(company) {
    return { type: "GET_ALL_CUSTOMERS_BY_COMPANY",paylod:company }
}
export function GetCustomerByCode(company) {
    return { type: "GET_CUSTOMER_BY_CODE",paylod:company }
}
export function AddCustomer(company) {
    return { type: "ADD_CUSTOMER",paylod:company }
}
export function UpdateCustomer(company) {
    return { type: "UPDATE_CUSTOMER",paylod:company }
}
export function RemoveCustomer(company) {
    return { type: "REMOVE_CUSTOMER",paylod:company }
}
