import React from 'react';
import { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {MDBIcon, MDBTable, MDBTableHead, MDBTableBody, MDBBtn } from 'mdb-react-ui-kit';
import { Link } from "react-router-dom";
import {getAllCustomersByCompany} from '../Redux/Customers/CustomersThunk'
import {calculationProfile} from '../Redux/ProfileCalculations/ProfileCalculationsThunk'
import { MDBInput } from 'mdbreact';
import {
  BrowserRouter,
  withRouter
} from "react-router-dom"; 
import './myStyle.css'
import { NavLink } from 'react-router-dom';

 export default function MyCustomers(Props){
  let Company = useSelector((store) => {return store.Companies.company }); 
  const dispatch = useDispatch();
  const [customers, setCustomers] = useState(useSelector((state) => { return state.customers }))
  const [profileCalculation,setProfileCalculation]=useState(!Props.profileCalculation?{}:Props.profileCalculation)
  const [theClassificaion,settheClassificaion]=useState(useSelector((state) => { return state.theClassificaion }))

  useEffect(async () => {

    debugger

    let C = await getAllCustomersByCompany(dispatch,Company.codeCompany);
    await console.log("C");
    await console.log(C);
    if (C) {
        setCustomers(C)
    }
  }, [])


const sendToClassification = async () => {
  
     let DateToday = document.getElementById("DateToday").value
     let DateStart = document.getElementById("DateStart").value
     let DateFinish = document.getElementById("DateFinish").value

     if(Company){
       debugger;
     const TheClassificaion = await calculationProfile(dispatch,Company.codeCompany,DateToday,DateStart,DateFinish);
     if(TheClassificaion){
     console.log("ttt")
     console.log(TheClassificaion)
      settheClassificaion(TheClassificaion)
    
     alert("אנא המתן לקוחותיך עוברים סיווג");
     
     return TheClassificaion

    
     }}
     else{
       alert("אנא הרשם או התחבר תחילה")
     }
  }
  const up=(e)=>{
    setProfileCalculation(e.target.value)
  }
  

 return <>
 
    <BrowserRouter>
    <div className='mysize'></div>
    <h2>הלקוחות שלי</h2>
    
    <lable className='bDate'>תאריך סיווג נוכחי</lable>
    <MDBInput type="date" id="DateToday" value={profileCalculation.calculationDate} onChange={(e) => { up(e) }} style={{width:"150px"}}></MDBInput>
    <label className='bDate'>בחר תאריך התחלה</label>
    <MDBInput type="date" id="DateStart" value={profileCalculation.fromDateFromDate} onChange={(e) => { up(e) }} style={{width:"150px"}}></MDBInput>
    <lable className='bDate'>בחר תאריך סיום</lable>
    <MDBInput type="date" id="DateFinish" value={profileCalculation.toDate} onChange={(e) => { up(e) }} style={{width:"150px"}}></MDBInput>
    
   <MDBBtn  onClick={()=>sendToClassification()}>סיווג</MDBBtn>
    <p></p>
 
    <div className="square border justify-content-center">
      {theClassificaion === undefined?
      <MDBTable striped>
        <MDBTableHead>
          <tr>
            <th scope='col'>#</th>
            <th scope='col'>שם:</th>
            <th scope='col'>אימייל:</th>
            <th scope='col'>פרופיל אחרון:</th>
          </tr>
        </MDBTableHead>
        <MDBTableBody>
          {customers === undefined ? <h6>wait...</h6> :
            customers.map((f, i) =>

              <tr>
                <th scope='row'>{++i}</th>
                <td>{f.nameCustomer}</td>
                <td>{f.emailCustomer}</td>
                <td>{f.profileCustomer}</td>
                
                < td>
                  <div class="d-grid gap-2 d-md-flex justify-content-md">
                
                    
                  </div>
                </td>
                
              </tr>
            )}
        </MDBTableBody>
      </MDBTable>


:
<MDBTable striped>
        <MDBTableHead>
          <tr>
            <th scope='col'>#</th>
            <th scope='col'>שם:</th>
            <th scope='col'>אימייל:</th>
            <th scope='col'>פרופיל אחרון:</th>
          </tr>
        </MDBTableHead>
        <MDBTableBody>
          {theClassificaion === undefined ? <h6>wait...</h6> :
            theClassificaion.map((f, i) =>

              <tr>
                <th scope='row'>{++i}</th>
                <td>{f.nameCustomer}</td>
                <td>{f.emailCustomer}</td>
                
                <td>{f.profileCustomer}</td>
                
                < td>
                  <div class="d-grid gap-2 d-md-flex justify-content-md">
                
                    
                  </div>
                </td>
                
              </tr>
            )}
        </MDBTableBody>
      </MDBTable>



}

    </div>
  <p></p>
    <MDBBtn><NavLink to="/ChartOfDate"></NavLink>צפייה בחישוב ממוצע</MDBBtn>

    <div class="d-flex justify-content-evenly">
    
   
    </div>
 
    </BrowserRouter>
  </>
}
