import { useDispatch,useSelector } from "react-redux";
import { store } from "../Reducers";
import {MakeAPurchase} from '../Invity/InvityAction'
import { AddToBasket } from  '../Customer/CustomerAction'

///////
export const defaultURL="http://localhost:10362"
//////
let invite;
export const DoInviteByBascet=async(dispatch,Basket)=>
{
    
    try{
      const id=useSelector((store)=>{return (store.customers.customer).IdCustomer});
     const invitePromise = axios.post(defaultURL+"?idCustomer="+id,Basket);
    const response = await invitePromise;
     invite = response.data;
    await dispatch(MakeAPurchase(invite));
    await dispatch(AddToBasket(null));
     return invite;
    }
   
    
   catch{
     alert("::::")

   }
   

}
