import { useEffect } from "react";
import { getAllCustomersFromServer,getCustomerFromserverById,addCustomer } from '../Redux/Customer/CustomersThunk'
import { useDispatch, useSelector } from 'react-redux';
import { MDBTable, MDBTableBody, MDBTableHead } from 'mdbreact';

 import { useState } from "react";
  
export default function ShowCustomers() {
 
  let myCustomer,myAddCustomer,myCustomerId
  const dispatch = useDispatch();
  let [c,setC]=useState([]);
  
   
  

  useEffect(async () => {
    let okmyCustomersFromServer = await getAllCustomersFromServer(dispatch);

    let myCustomersFromServer = await getAllCustomersFromServer(dispatch);
    await console.log(myCustomersFromServer)
    await setC(myCustomersFromServer)
    // await console.log(myCustomersFromServer)
    // await setcustomersList(myCustomersFromServer)
    // console.log(customersList)

    
    // myCustomer = await getCustomerFromserverById(dispatch,1,"611611");
    // await console.log(myCustomer)
    //   await setoneCustomer(myCustomer)

    // myCustomerId = await getCustomerFromserverById1(dispatch,1);
    // await console.log(myCustomerId)
    //   await setoneCustomer(myCustomerId)

    //   myAddCustomer = await addCustomer(dispatch,5,"sh","711711","322461309");   
    //         await console.log("הוספת לקוח  "+myAddCustomer);
    //         await setcustomersList(myAddCustomer);

      }, [])
     return<>
     
     <h2>לקוחות</h2>
     <MDBTableHead>
        <tr>
          <th>קוד לקוח</th>
          <th>שם</th>
          <th>סיסמא</th>
          <th>מספר אשראי</th>
          
          
        </tr>
      </MDBTableHead>
     
     {

      c != undefined ?
      c.length > 0 &&
      c.map((item) =>
           
      <MDBTable responsive>
      
      <MDBTableBody>
        <tr>
          <td>{item.idCustomer}</td>
          <td>{item.nameCustomer}</td>
          <td>{item.passwordCustomer}</td>
          <td>{item.creditcardCustomer}</td>
          
        </tr>
      </MDBTableBody>
    </MDBTable>
           
      )   
         : <div>אין לקוחות</div>
 
     }
   </>
   
 }
