import produce from 'immer';

const ProductsInitialState={
    product:{},
    products:[],
    productByCategories:[]

}


export const ProductsReducer=produce((state,action)=>{
    switch(action.type)
    {
        case "GET_PRODUCT":state.product=action.payload;break;
        case "GET_ALL_PRODUCTS":state.products.push(action.payload);break;
        case "ADD_PRODUCT":state.products.push(action.payload);break;
        case "REMOVE_PRODUCT":state.products.push(action.payload);break;
        case "UPDATE_PRODUCT":state.products.push(action.payload);break;
        case "GET_BY_CATEGORIE":state.productByCategories.push(action.payload);break;
    }
},ProductsInitialState)
