import React from "react";
import { MDBContainer, MDBRow, MDBCol, MDBBtn } from 'mdbreact';
 import  {useState} from 'react';
 import { useDispatch } from "react-redux";
 import {updateProduct,getProductById} from '../Redux/Product/ProductsThunk'
 import { useParams } from "react-router";
 import { useEffect } from "react";

const ProductUpdate = (code) => {
    const dispatch=useDispatch();
    let c=code.match.params.id;
    
    let [p,setP]=useState({});

   useEffect(async () => {
    let p1=await getProductById(dispatch,c); 
    console.log(p1);
     setP(p1);
   }, [])



   
   const newProducts=async(dispatch)=>{
    
    let IdProduct=document.getElementById("i9").value
    let NameProduct=document.getElementById("i10").value
    let IdCategorie=document.getElementById("i11").value
    let PriceProduct=document.getElementById("i12").value
    let ImageProduct=document.getElementById("i5").value
    let SizeProduct=document.getElementById("i6").value
    let AmountProduct=document.getElementById("i7").value
    
   if(IdProduct)
   {
   
    
    const product=await updateProduct(dispatch,IdProduct,NameProduct ,IdCategorie,PriceProduct,ImageProduct,SizeProduct,AmountProduct);
    alert("המוצר עודכן בהצלחה");
     return product
   }
   else
   {
  
    alert("קוד מוצר הינו שדה חובה להוספת מוצר")
    
  }
  

  }
const up=(e)=>{
    setP(e.target.value)
}
return (
<MDBContainer>
  <MDBRow>
    <MDBCol md="6">
      <form>
        <p className="h4 text-center mb-4">עדכון לקוח</p>
        <label htmlFor="defaultFormRegisterNameEx" className="grey-text" >
          Product id
        </label>
        <input type="text" id="i9" value={p.idProduct}  className="form-control" name= "ii9" onChange={(e)=>up(e)} />
    
        <br />
        <label htmlFor="defaultFormRegisteridEx" className="grey-text">
        Product name
        </label>
        <input type="text" id="i10" value={p.nameProduct} className="form-control" name= "ii10" onChange={(e)=>up(e)}/>
        <br />
        <label htmlFor="defaultFormRegistercreaditCardEx" className="grey-text">
          Product Id Of Categorie
        </label>
        <input type="text" id="i11" value={p.idCategorie} className="form-control" name= "ii11" onChange={(e)=>up(e)}/>
        <br />
        <label htmlFor="defaultFormRegisterPasswordEx" className="grey-text">
          Product Price
        </label>
        <input type="text" id="i12" value={p.priceProduct} className="form-control" name= "ii12" onChange={(e)=>up(e)}/>
        <div className="text-center mt-4"></div>
        <br />
        <label htmlFor="defaultFormRegisterPasswordEx" className="grey-text">
          Product Image
        </label>
        <input type="text" id="i5" value={p.imageProduct} className="form-control" name= "ii15"/>
        <div className="text-center mt-4">
        <br />
        <label htmlFor="defaultFormRegistercreaditCardEx" className="grey-text">
        Product Size
        </label>
        <input type="text" id="i6" value={p.sizeProduct} className="form-control" name= "ii16"/>
        <br />
        <label htmlFor="defaultFormRegistercreaditCardEx" className="grey-text">
        Product Amount 
        </label>
        <input type="text" id="i7" value={p.amountProduct} className="form-control" name= "ii7"/>

        
          <MDBBtn color="deep-orange" className="mb-3" type="submit" onClick={() => newProducts(dispatch)}>
          עדכן מוצר
                </MDBBtn>
        </div>
      </form>
    </MDBCol>
  </MDBRow>
</MDBContainer>
);
};

export default ProductUpdate;
