import { useEffect } from "react";
import { getAllProductsFromServer, getProductById, addProduct, updateProduct, removeProduct, getByCategorie } from '../Redux/Product/ProductsThunk'
import { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { MDBCard, MDBCardBody, MDBCardImage, MDBCardTitle, MDBCardText, MDBRow, MDBCol, MDBIcon } from 'mdbreact';
import {
  BrowserRouter,
  Switch,
  Route,
  Link,
  NavLink,
  
} from "react-router-dom";
  
export default function ShowProducts() {

  
  let myProduct, myUpdateProduct, myAddProduct, myRemoveProduct, myProductOfCategory;
  const dispatch = useDispatch();
  //כל המוצרים
  let myProductsFromServer = useSelector((store) => {
    if (store.products.products != undefined && store.products.products.length > 0)
      return store.products.products[0].data
    else
      return undefined
  }
  )


  useEffect(async () => {
   
    let okmyProductsFromServer = await getAllProductsFromServer(dispatch);
    

  }, [])
  // setOneProduct(myProductsFromServer);
  // console.log("productList")
  // console.log(OneProduct)
  // console.log("productList1")
  // console.log(productList1)

  //  myProduct = await getProductById(dispatch, 1);
  //  await console.log(myProduct);
  //  await setOneProduct(myProduct);


  //  myUpdateProduct = await updateProduct(dispatch, "146", ",,,", "1", "100", "ggg", 20, 50);
  //  await console.log("עדכון מוצר " + myUpdateProduct);
  //  await setProductList(myUpdateProduct);

  //  myAddProduct = await addProduct(dispatch, "146", ",,,", "1", "300", "PPPP", 30, 50);
  //  await console.log("הוספת מוצר  " + myAddProduct);
  //  await setProductList(myAddProduct);


  //  myRemoveProduct = await removeProduct(dispatch, 2);
  //  await console.log("הסרת מוצר " + myRemoveProduct);
  //  await setProductList(myRemoveProduct);


  //  myProductOfCategory = await getByCategorie(dispatch, 1);
  //  await console.log("מוצרים לפי קטגוריה " + myProductOfCategory);
  //  await setproductsInThisCategorie(myProductOfCategory);
  // }, [])
  // console.log("productList1")
  // console.log(productList1)

  return <>
    <h2 style={hstyle}>מוצרים</h2>
    {
        myProductsFromServer != undefined ?
        myProductsFromServer.length > 0 &&
        myProductsFromServer.map((item) =>
          

        <div class="card" style={pStyles}>
                        <img src={`http://localhost:10362/Images/${item.imageProduct}`} style={{ width: "400px",height:"250px" }}
                            class="card-img-top"
                            alt="Fissure in Sandstone"
                        />
                        <div class="card-body">
                            <h5 class="card-title">{item.nameProduct}</h5>
                            <p class="card-text">
                                Some quick example text to build on the card title
                                <br></br> and make up the bulk of the
                                card's content.
                            </p>
                            
                             <a href="#!" class="btn btn-primary">פרטים נוספים
                            </a> 
                        </div>
                   

                </div>
     
           
        )
        : <div>אין מוצרים</div>
        }


  </>
}
const pStyles = {
  float:"right",
      marginLeft: "5px",
      marginRight: "5px",
      margin:'20px 3%'
      
  }
  const hstyle={
    textAlign:'center',
    bacroundColor:'red',
    margin:'25px 0px',
 
  }
