import { combineReducers, createStore } from "redux";
import { CustomersReducer } from "./Customer/CustomerReducer";
import { ProductsReducer } from "./Product/ProductReducer";
import { CategoriesReducer } from "./Categorie/CategorieReducer";

const reducers = combineReducers(
    {
        customers: CustomersReducer,
        products:ProductsReducer,
        categories:CategoriesReducer
    }
)

//בניית הסטור- המחסן הגלובאלי- שיכיר את כל הנתונים
export const store = createStore(reducers);

//לצורך בדיקה בלבד- בדפדפן
window.store = store;
