 import {useEffect} from "react";
 import {getAllCategoriesFromServer,getCategorieById,addCategorie,apdateCategorie,removeCategorie} from '../Redux/Categorie/CategoriesThunk'
 import {useState} from 'react';
 import {useDispatch} from 'react-redux';

 export default function ShowCategories() {
    
    let myCategoriesFromServer
    let myCategorie,myUpdateCategorie,myAddCategorie,myRemovecategorie;
    const [categoriesList,setcategoriesList]=useState ([])
    const [oneCategorie,setOneCategorie] = useState({})

    const dispatch = useDispatch();

    useEffect(async () => {

        myCategoriesFromServer = await getAllCategoriesFromServer(dispatch);
        await console.log(myCategoriesFromServer);
        await setcategoriesList(myCategoriesFromServer);

        myCategorie = await getCategorieById(dispatch,1);
        //await console.log(myCategorie);
        await setOneCategorie(myCategorie);

        debugger;
        myAddCategorie = await addCategorie(dispatch,3,"www");
        await console.log("הוספת קטגוריה"+myAddCategorie);
        await setcategoriesList(myAddCategorie);
   

        myUpdateCategorie = await apdateCategorie(dispatch,"2","hhh");
        await console.log("עדכון קטגוריה"+myUpdateCategorie);
        await setcategoriesList(myUpdateCategorie);
    

        myRemovecategorie = await removeCategorie(dispatch,"2");
        await console.log("הסרה קטגוריה"+myRemovecategorie);
        await setcategoriesList(myRemovecategorie);
  
    }, [])
    
    return<>
    {/* <h1>Show categories to users</h1> */}
    {/* <div>
 { categoriesList.map((x)=>(

        <h3> {x.nameCategorie}  </h3>))

} <h2>אובייקט: {oneCategorie.nameCategorie}</h2>

 </div> */}
</>
}
