import React, { useEffect } from "react";
import { MDBContainer, MDBRow, MDBCol, MDBBtn, MDBInput } from 'mdbreact';
import { useState } from 'react';
import { useDispatch } from "react-redux";
import { addCompany ,updateCompany } from '../Redux/Companies/CompaniesThunk'
import { MDBModal, MDBModalBody, MDBModalHeader, MDBModalFooter } from 'mdbreact';
import { useSelector } from "react-redux";


export default function SignUp (){
  debugger
  let User = useSelector((store) => {return store.Users.use }); 
  debugger
  console.log("User")
  console.log(User.idUser)
  const [modal14, setModal14] = useState(true);
  const [errors, setErrors] = useState({IdUser: "שדה חובה", PrivateName: "שדה חובה",FamilyName: "שדה חובה",CityAdress: "שדה חובה", StreetAdress: "שדה חובה", NumberAdress: "שדה חובה",DateOfBirth: "שדה חובה",Phone: "שדה חובה",MobilePhone: "שדה חובה" });
  const [user, setUser] = useState({ idUser:"",privateName:"",familyName:"",cityAdress:"",streetAdress:"",numberAdress:"",dateOfBirth:"",numberAdress:"",phone:"",mobilePhone:""});
  const dispatch = useDispatch();

  
  useEffect(async () =>{
  if(!User){
    User.IdUser="";
    User.PrivateName="";
    User.FamilyName="";
    User.CityAdress="";
    User.StreetAdress="";
    User.NumberAdress="";
    User.DateOfBirth="";
    User.Phone="";
    User.MobilePhone="";
  }
  setUser(User);
  },[])

  const newUser = async () => {
     debugger
     let PrivateName = document.getElementById("PrivateName").value
     let FamilyName = document.getElementById("FamilyName").value
     let CityAdress = document.getElementById("CityAdress").value
     let StreetAdress = document.getElementById("StreetAdress").value
     let NumberAdress = document.getElementById("NumberAdress").value
     let DateOfBirth = document.getElementById("DateOfBirth").value
     let Phone = document.getElementById("Phone").value
     let MobilePhone = document.getElementById("MobilePhone").value

     
    if(User.idUser){
    
    if (PrivateName&&FamilyName&&CityAdress&&StreetAdress&&NumberAdress&&DateOfBirth&&Phone&&MobilePhone){
      let User=null;
      let ProfileCalculations=null;
      const user=await updateUser(dispatch,User.idUser,FamilyName,CityAdress,StreetAdress,NumberAdress,DateOfBirth,Phone,MobilePhone) 
      if(user)
      alert("הפרטים עודכנו בהצלחה");
      setModal14(!modal14);
    
      }
      else{
        alert("הפרטים לא עודכנו אנא וודא שהשדות לא ריקים  ותקינים")
      }
    }
    ////
    else{
      if(errors.erroruserName1===""&&errors.errorEmail1===""&&errors.errorEmail2===""&&errors.erroruserName===""&&errors.pass1===""&&errors.pass2===""){
     if (NameCompany&&PrivateEmail&&CustomerServiceEmail&&CustomerServiceEmailPassword&&UserName&&UserPassword) {
      const user = await addUser(dispatch,NameCompany,PrivateEmail,CustomerServiceEmail,CustomerServiceEmailPassword,UserName,UserPassword);
      alert("ברוך הבא " + UserName + " לאתרינו מקווים שתהיה לך את חווית הגלישה הטובה");
      setModal14(!modal14);
      return company
     }}
      
    else {

      alert("אנא מלא את כל השדות ובדוק שכולם תקינים");

    }}
  }

  const validEmail1 = (event) => {
    var myemail = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;
    if (!event.target.value)
      setErrors({ ...errors, errorEmail1: 'שדה חובה*' })
    else if (event.target.value.match(myemail)) { setErrors({ ...errors, errorEmail1: "" }) }
    else if(!event.target.value.match(myemail)){
      setErrors({ ...errors, errorEmail1: "אימייל לא תקין" })
    }
    setCompany({ ...company, privateEmail: event.target.value })
  }
  

  const validEmail2 = (event) => {
    var myemail = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;
    if (!event.target.value)
      setErrors({ ...errors, errorEmail2: 'שדה חובה*' })
    else if (event.target.value.match(myemail)) { setErrors({ ...errors, errorEmail2: "" }) }
    else if (!event.target.value.match(myemail)) {
      setErrors({ ...errors, errorEmail2: "אימייל לא תקין" })
    }
    setCompany({ ...company, customerServiceEmail: event.target.value })
  }



  //בדיקת תקינות של שם
  const validateName = (event) => {
    let pattern="^(?=[a-zA-Z0-9א-ת ]{4,10}$)(?!.*[_.]{2})[^_.].*[^_.]$";

    
      if (!event.target.value)
        setErrors({ ...errors, erroruserName: "שדה חובה*" });
      else if (!event.target.value.match(pattern)) 
        setErrors({ ...errors, erroruserName: "האורך חייב להיות בין 4 ל10 תווים אין להשתמש ב_ או . " });
      else  if (event.target.value.match(pattern)){
        setErrors({ ...errors, erroruserName: "" });
      
        

      }
      setCompany({ ...company, userName:event.target.value})
    
    }
    
    const validateName1 = (event) => {
      let pattern="(?=[a-zA-Z0-9א-ת ]{2,9}$)";
  
      
        if (!event.target.value)
          setErrors({ ...errors, erroruserName1: "שדה חובה*" });
        else if (!event.target.value.match(pattern)) 
          setErrors({ ...errors, erroruserName1: "לא יכול להכיל רק תו אחד " });
        else  if (event.target.value.match(pattern)){
          setErrors({ ...errors, erroruserName1: "" });
  
        }
        setCompany({ ...company, nameCompany:event.target.value})
      
      }
      
  //בדיקת תקינות של סיסמא
  const validatePass1 = (event) => {

    var passw = /^[A-Za-z]\w{5,20}$/;
    if (!event.target.value)
    setErrors({ ...errors, pass1: 'שדה חובה*' })
    else if(!event.target.value.match(passw)) 
      setErrors({ ...errors, pass1: 'סיסמא צריכה להיות בין 5 ל 20 תווים . מכילה רק מספרים ואותיות. אות באנגלית חובה בהתחלה  ' })
    
   else if (event.target.value.match(passw)) 
      setErrors({ ...errors, pass1: "" });
    
    
    setCompany({ ...company, customerServiceEmailPassword: event.target.value })
  }

  const validatePass2 = (event) => {

    var passw = /^[A-Za-z]\w{5,20}$/;
    if (!event.target.value)
    setErrors({ ...errors, pass2: 'שדה חובה*' })
    else if (event.target.value.match(passw)) {
      setErrors({ ...errors, pass2: "" });
    }

    else if((!event.target.value.match(passw))) {
      setErrors({ ...errors, pass2: 'סיסמא צריכה להיות בין 5 ל 20 תווים . מכילה רק מספרים ואותיות באנגלית אות חובה בהתחלה ' })
    }
    setCompany({ ...company, userPassword: event.target.value })
  }

  const up=(event)=>{
    if (!event.target.value)
    setErrors({ ...errors, nameCompany1: 'שדה חובה*' })
   else setCompany(event.target.value)
  }
   
  
  return (
    <>
      
      <MDBContainer>
        <MDBModal isOpen={modal14} toggle={() => { setModal14(!modal14) }} centered>
          
          <MDBModalHeader toggle={() => { setModal14(!modal14); }}>
            {
              User.idUser?
              <>
            טופס עדכון   
              </>
              :<>טופס הרשמה </>
            }
            </MDBModalHeader>
          <MDBModalBody>
            <MDBContainer>
              <MDBRow>
                <MDBCol md="6">
                  <form>

                    <div className="grey-text">
                      <label>שם פרטי</label>
                      <MDBInput  id="PrivateName" value={user.privateName} onChange={(e) => { validateName1(e) }} icon="user" group type="text" validate error="wrong"
                        success="right" />
                        {/* <p style={{color:"red"}}>{errors.erroruserName1} </p> */}
                        <label>שם משפחה</label>
                      <MDBInput  id="FamilyName" value={user.familyName} onChange={(e) => { validEmail1(e) }} icon="envelope" group type="email" validate error="wrong"
                        success="right"  /> 
                        {/* <p style={{color:"red"}}>{errors.errorEmail1}</p> */}
                         <label>עיר</label>
           
                      <MDBInput id="CityAdress" value={user.cityAdress} onChange={(e) => { validEmail2(e) }} icon="envelope" group type="email" validate error="wrong"
                        success="right" />
                        {/* <p style={{color:"red"}}>{errors.errorEmail2}</p> */}
                    
                        <label>רחוב</label>
                        <div  className="cm">
                      <MDBInput id="StreetAdress" value={user.streetAdress} onChange={(e) => { validatePass1(e) }} icon="lock" group type="text" validate />
                     {/* <p style={{color:"red"}}>{errors.pass1} </p> */}
                     
                      <label>מספר דירה</label>
                      <MDBInput  id="NumberAdress" value={user.numberAdress} onChange={(e) => { validateName(e) }} icon="user" group type="text" validate error="wrong" success="right" />
                      {/* <p style={{color:"red"}}>{errors.erroruserName}</p> */}
                      <label>תאריך לידה </label>
                      <MDBInput  id="DateOfBirth" value={user.dateOfBirth} onChange={(e) => { validatePass2(e) }} icon="lock" group type="text" validate  />
                      {/* <p style={{color:"red"}}>{errors.pass2}</p> */}
                    
                    <label> טלפון </label>
                      <MDBInput  id="Phone" value={user.phone} onChange={(e) => { validatePass2(e) }} icon="lock" group type="text" validate  />
                      {/* <p style={{color:"red"}}>{errors.pass2}</p> */}
                    <label>פלאפון נייד  </label>
                      <MDBInput  id="MobilePhone" value={user.mobilePhone} onChange={(e) => { validatePass2(e) }} icon="lock" group type="text" validate  />
                      {/* <p style={{color:"red"}}>{errors.pass2}</p> */}
                    <div className="text-center">
                    </div>
                    </div>
                    </div>
                  </form>
                </MDBCol>
              </MDBRow>
            </MDBContainer>
          </MDBModalBody>
          <MDBModalFooter>
            {
              Company.codeCompany?
            <MDBBtn color="secondary" onClick={() => newUser()}>עדכן</MDBBtn>
            :<MDBBtn color="secondary" onClick={() => newUser()}>הרשם</MDBBtn>
}
          </MDBModalFooter>
        </MDBModal>
      </MDBContainer>
    </>
  );
}
