import React from "react";
import { MDBContainer, MDBRow, MDBCol, MDBBtn, MDBInput } from 'mdbreact';
import { getCustomerFromserverById } from '../Redux/Customer/CustomersThunk';


const IsMember = async() => {

  const id = document.getElementById("id").value;
  const pass = document.getElementById("password").value;

  const customer =  await getCustomerFromserverById(id, pass);
  // console.log(user);
//למה הוא מדפיס גם את האלס ??????????????
  if (customer) {
    console.log(customer);
    return;
  }
  else
   {
    console.log("??????00000000000?????");
    return;
  }

}


const SignIn = () => {
  return (
    <MDBContainer>
      <MDBRow>
        <MDBCol md="6">
          <form>
            <p className="h5 text-center mb-4">Sign in</p>
            <div className="grey-text">
              <MDBInput label="Your id" icon="customer" group type="text" validate error="wrong"
                success="right" id="name" />

              <MDBInput label="Your password" icon="lock" group type="password" validate id="pass" />
            </div>


            <div className="text-center">
              <MDBBtn color="primary" onClick={() => IsMember()}>Sign In</MDBBtn>
            </div>
          </form>
        </MDBCol>
      </MDBRow>
    </MDBContainer>
  );
};

export default SignIn;
