import React from "react";
import { useState } from "react/cjs/react.development"

import {
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBCard,
  MDBCardBody,
  MDBModalFooter,
  MDBIcon,
  MDBCardHeader,
  MDBBtn
} from "mdbreact";


const FormPage = (Props) => {
  const [errors, setErrors]=useState({email:"שדה חובה", pass:"שדה חובה"});
    const [user, setUser]=useState(!Props.User?{}:Props.User);
const newUser=()=>{
   <FormPage></FormPage>
  //alert("new user");
}

  const validateName=(event)=>{

    if(!event.target.value)
    setErrors({...errors, UserName:"שדה חובה"});
    else if (event.target.value.includes(" "))
    setErrors({...errors, UserName:"אין לשים רווח"});
else {
    setErrors({...errors, UserName:"תקין!"});
}
setUser({...user, UserName: event.target.value })
}

const validatePass=(event)=>
{
    
var passw = /^[A-Za-z]\w{7,14}$/;
if (event.target.value.match(passw)) {
  setErrors({...errors, pass: "תקין!"} );
  // setUser({...user, UserPass: event.target.value })
}
else {
  setErrors({...errors, pass: 'Wrong...!! password password between 7 to 16 characters which contain only characters, numeric digits, underscore and first character must be a letter' })
}
setUser({...user, UserPass: event.target.value })

}
const validateEmail=(event)=>
{
    if (!event.target.value) {
        setErrors({...errors, email: "שדה חובה!"} );

    } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(event.target.value)) {
        setErrors({...errors, email: "לא תקין!"} );
    }
    else {
        setErrors({...errors, email: "תקין!"} );
       
    } setUser({...user, UserEmail: event.target.value })
}

  return (
   
    <MDBContainer>
      <MDBRow>
        <MDBCol md="6">
          <MDBCard>
            <MDBCardBody>
              <MDBCardHeader className="form-header warm-flame-gradient rounded">
                {!(Props.User.UserPass)?
                <h3 className="my-3">
                  <MDBIcon icon="lock" /> Sign up
                </h3>
                :
                <h3 className="my-3">
                  <MDBIcon icon="lock" /> :טופס עדכון
                </h3>
}
               

              </MDBCardHeader>
              <label
                htmlFor="defaultFormEmailEx"
                className="grey-text font-weight-light"
              >
                Your name
              </label>
              <input
                type="text"
                id="defaultFormNameEx"
                className="form-control"
                value={user.UserName} onChange={(e)=>validateName(e)}  name="UserName"
              ></input>
              <p > *{errors.UserName}</p>

              <label
                htmlFor="defaultFormPasswordEx"
                className="grey-text font-weight-light"
              >
                Your password
              </label>
              <input
                type="password"
                id="defaultFormPasswordEx"
                className="form-control"
                name="pass" 
                value={user.UserPass}
                 onChange={(e)=>validatePass(e)}
              />
              <p>{errors.pass}</p>


              <label
                htmlFor="defaultFormEmailEx"
                className="grey-text font-weight-light"
              >
                Your email
              </label>
              <input
                type="email"
                id="defaultFormEmailEx"
                className="form-control"
                name="email" value={user.UserEmail} onChange={(e)=>validateEmail(e)} 
              ></input>
              <p > *{errors.email}</p>
              <div className="text-center mt-4">
                <MDBBtn color="deep-orange" className="mb-3" type="submit">
                  Login
                </MDBBtn>
              </div>

              <MDBModalFooter>
                <div className="font-weight-light">
                  <p>Not a member? </p>
                  <MDBBtn color="deep-orange" className="mb-3" type="submit" onClick={()=>newUser()}>
                  Sign Up
                </MDBBtn>
                  <p>Forgot Password?</p>
                </div>
              </MDBModalFooter>
            </MDBCardBody>
          </MDBCard>
        </MDBCol>
      </MDBRow>
    </MDBContainer>
    
  );
};

export default FormPage;


FormPage.defaultProps={
  Customers:{}
}
