import React from "react";
import { Pie } from "react-chartjs-2";
import { MDBContainer } from "mdbreact";
import { useDispatch } from "react-redux";
import {pnu} from '../Redux/ProfileCalculations/ProfileCalculationsThunk'


import './myStyle.css'



class LittleChartsPage extends React.Component{

  async componentDidMount() {

    const c = this.props.ProfileCalculations;
      const myData = await pnu(c);
      this.state.dataPie.datasets[0].data=myData
    
  
  const MapStateToProps = state=>{
    return{
      c: state.c
    }
  }
  }
  state = {
    dataPie: {
      labels: ["ביקורות חיוביות", "ביקורות שליליות", "ביקורות נטרליות"],
      datasets: [
        {
          data: [],
          backgroundColor: [
            "pink",
            "#46BFBD",
            "#FDB45C",


          ],
          hoverBackgroundColor: [
            "gray",
            "gray",
            "gray"
          ]
        }
      ]
    }
  }

  render() {
    return (
      <MDBContainer>
        <h3 className="mt-5">תרשים סיווג נוכחי</h3>
        <Pie  data={this.state.dataPie} options={{ responsive: true }} />
      </MDBContainer>
    );
  }
}

export default LittleChartsPage;
