import React, { Component, useState } from 'react';
import {  MDBRow, MDBCol, MDBInput } from 'mdbreact';
import { MDBContainer, MDBBtn, MDBModal, MDBModalBody, MDBModalHeader, MDBModalFooter } from 'mdbreact';
import { useDispatch } from 'react-redux';
import {getCompanyByNameAndPassword} from '../Redux/Companies/CompaniesThunk'
import { useHistory } from 'react-router';
import { useSelector } from "react-redux";
import { useEffect } from 'react';
import {
  BrowserRouter,
  Switch,
  Route,
  Link,
  NavLink,
  withRouter
} from "react-router-dom";




 export default  withRouter(function SignIn(props){
  let Company = useSelector((store) => {return store.Companies.company }); 
  const [modal14,setModal14]=useState(true);
  const history=useHistory();
  const dispatch=useDispatch();
  useEffect(async () =>{
    if(!Company){
      alert("לקוח יקר הינך רשום כבר במערכת");
      setModal14(!modal14);
    }
    },[])
  
  const ModalPage = async() => {  
    const PrivateEmail = document.getElementById("PrivateEmail").value;
    const UserName = document.getElementById("UserName").value;
    const Password = document.getElementById("Password").value;
    const company =  await  getCompanyByNameAndPassword(dispatch,PrivateEmail,UserName, Password);
    
  if (company) 
  {
    alert("ברוך הבא "+UserName);
     setModal14(!modal14);
  return company;
  }
  else
   {
     alert("אחד או יותר מהנתונים שגוי...");
    
    return;
  }
 
  }
 

  return (
    <BrowserRouter>
      <MDBContainer>
        <MDBModal isOpen={modal14} toggle={()=>{setModal14(!modal14)}} centered>
          <MDBModalHeader toggle={()=>{setModal14(!modal14);}}>התחברות</MDBModalHeader>
          <MDBModalBody>
          <MDBContainer>
  <MDBRow>
    <MDBCol md="6">
      <form>
       
        <div className="grey-text">
         <label>אימייל</label>
        <MDBInput  id="PrivateEmail" icon="envelope" group type="text" validate error="wrong"
            success="right" />
       <label>שם משתמש</label>
          <MDBInput  id="UserName" icon="user" group type="text" validate error="wrong"
            success="right" />
            <label>סיסמא</label>
          <MDBInput id="Password" icon="lock" group type="password" validate />
        </div>
      </form>
    </MDBCol>
  </MDBRow>
</MDBContainer>
          </MDBModalBody>
          <MDBModalFooter>
            <MDBBtn color="secondary" onClick={()=>ModalPage(dispatch)}>התחבר</MDBBtn>
            <MDBBtn color="secondary" onClick={()=>{setModal14(!modal14);
             props.history.push("/SignUp")}}>הרשם</MDBBtn>

          </MDBModalFooter>
        </MDBModal>
      </MDBContainer>
      </BrowserRouter>
    );
  })
