import React, { useEffect, useState } from "react";
import { Pie } from "react-chartjs-2";
import { MDBContainer } from "mdbreact";
import { useDispatch } from "react-redux";
import { allTheVaccinations } from '../Redux/ProfileCalculations/ProfileCalculationsThunk';
import './myStyle.css';

const LittleChartsPage = () => {
  const dispatch = useDispatch();
  const [pieData, setPieData] = useState({
    labels: ["מחוסני חיסוו רביעי","מחוסני חיסון שלישי ", "מחוסני חיסון שני ", "מחוסני חיסון ראשון"],
    datasets: [
      {
        data: [],
        backgroundColor: [
          "pink",
          "#46BFBD",
          "#FDB45C",
          "yello"
        ],
        hoverBackgroundColor: [
          "gray",
          "gray",
          "gray",
          "gray"
        ]
      }
    ]
  });

  useEffect(() => {
    async function fetchData() {
      let myData = await dispatch(allTheVaccinations());
      setPieData(prevPieData => ({
        ...prevPieData,
        datasets: [
          {
            ...prevPieData.datasets[0],
            data: myData
          }
        ]
      }));
    }

    fetchData();
  }, [dispatch]);

  return (
    <MDBContainer>
      <Pie className="ccc" data={pieData} options={{ responsive: true }} />
    </MDBContainer>
  );
};

export default LittleChartsPage;
