import React from "react";
import { MDBContainer, MDBRow, MDBCol, MDBBtn } from 'mdbreact';

const FormPage = () => {
return (
<MDBContainer>
  <MDBRow>
    <MDBCol md="6">
      <form>
        <p className="h4 text-center mb-4">Sign up</p>
        <label htmlFor="defaultFormRegisterNameEx" className="grey-text">
          Your name
        </label>
        <input type="text" id="defaultFormRegisterNameEx" value={customer.CustomerName} onChange={(e)=>{validateName(e)}} className="form-control" />
        <p > {errors.CustomerName}</p>
        <br />
        <label htmlFor="defaultFormRegisteridEx" className="grey-text">
          Your id
        </label>
        <input type="text" id="defaultFormRegisteridEx" value={customer.CustomerId} className="form-control" />
        <br />
        <label htmlFor="defaultFormRegistercreaditCardEx" className="grey-text">
          Your creaditCard
        </label>
        <input type="text" id="defaultFormRegistercreaditCardEx" value={customer.CustomerCreditcard} className="form-control" />
        <p > {errors.CustomerPassword}</p>
        <br />
        <label htmlFor="defaultFormRegisterPasswordEx" className="grey-text">
          Your password
        </label>
        <input type="password" id="defaultFormRegisterPasswordEx" value={customer.CustomerPassword} onChange={(e)=>{validatePass(e)}} className="form-control" />
        <div className="text-center mt-4">
          <MDBBtn color="deep-orange" className="mb-3" type="submit" onClick={() => newCustomer()}>
          Register
                </MDBBtn>
        </div>
      </form>
    </MDBCol>
  </MDBRow>
</MDBContainer>
);
};

export default FormPage;
