export function GetCustomer(customer) {
    return { type: "GET_CUSROMER",payload:customer }
}
export function GetCustomerId(customer) {
    return { type: "GET_CUSROMER_Id",payload:customer }
}
export function GetAllCustomers(customer) {
    return { type: "GET_ALL_CUSTOMERS",payload:customer }
}
export function AddCustomer(customer) {
    return { type: "ADD_CUSTOMER",payload:customer }
}
export default function isMenneger(value) {
    return { type: "IS_MENNEGER",payload:value }
}
