import React from 'react';
import {
  MDBCol,
  MDBContainer,
  MDBRow,
  MDBCard,
  MDBCardText,
  MDBCardBody,
  MDBCardImage,
  MDBBtn,
  MDBBreadcrumb,
  MDBBreadcrumbItem,
  MDBProgress,
  MDBProgressBar,
  MDBIcon,
  MDBListGroup,
  MDBListGroupItem,
  MDBTable,
  MDBTableHead,
  MDBTableBody,
  MDBFile 
} from 'mdb-react-ui-kit';

import { getUserById } from '../Redux/Users/UsersThunk';
import { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getVaccinationById } from '../Redux/Vaccinations/VaccinationsThunk';


export default function ProfilePage() {

    let User = useSelector((store) => {return store.Users.user }); 
    const dispatch = useDispatch();
    const [user,setUser]=useState([]);
    const [user2,setUser2]=useState({});

    const [myFile, setMyfile] = useState();
    //העלאת תמונה
    const uploadFile = (e) => {
        setMyfile(e.target.files[0]);
    }
    
    const saveImge = async (event) => {

        if (myFile == null) {
            alert("יש לבחור קובץ");
        }
        else {
            try {

             //העלאת תמונה לשרת
        const formData = new FormData();
        formData.append('file', myFile, myFile.name);
        await axios.post("https://localhost:44315/api/Users/upload", formData, { reportProgress: true, observe: 'events' }).
          then(x => {
            // console.log(x);

          })
                }

              catch {

                    }
    
           }







    useEffect(async () => {
       
        let U = await getVaccinationById(dispatch,User.idUser);
        let U2 = await getUserById(dispatch,User.idUser);
        if(U2){
         setUser(U)
         setUser2(U2)
        }
        else{
            alert("אינך מחובר למערכת אנא התחבר או הרשם תחילה")
        }
        }, [])


  return (
    <section style={{ backgroundColor: '#eee' }}>
      <MDBContainer className="py-5">
        <MDBRow>
          <MDBCol>
            <MDBBreadcrumb className="bg-light rounded-3 p-3 mb-4">
              <MDBBreadcrumbItem>
                <a href='#'>Home</a>
              </MDBBreadcrumbItem>
              <MDBBreadcrumbItem>
                <a href="#">User</a>
              </MDBBreadcrumbItem>
              <MDBBreadcrumbItem active>User Profile</MDBBreadcrumbItem>
            </MDBBreadcrumb>
          </MDBCol>
        </MDBRow>

        <MDBRow>
          <MDBCol lg="4">
            <MDBCard className="mb-4">
              <MDBCardBody className="text-center">
                <MDBCardImage
                  src="https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-chat/ava3.webp"
                  alt="avatar"
                  className="rounded-circle"
                  style={{ width: '150px' }}
                  fluid />
                <p className="text-muted mb-1">{user2.idUser}</p>
                <p className="text-muted mb-1">{user2.privateName} {user2.familyName}</p>
                <p className="text-muted mb-1">{user2.cityAdress} {user2.streetAdress} {user2.numberAdress}</p>
                <p className="text-muted mb-1">תאריך לידה: {user2.dateOfBirth}</p>
                <p className="text-muted mb-1">{user2.phone},{user2.mobilePhone}</p>
                <div className="d-flex justify-content-center mb-2">
                <MDBBtn outline className="ms-1"><MDBFile onChange={(event) => { uploadFile(event) }} />העלאת תמונה</MDBBtn>
                <MDBBtn outline className="ms-1" onClick={(event) => { saveImge(event) }}>שמור תמונה</MDBBtn>
                </div>
              </MDBCardBody>
            </MDBCard>
          </MDBCol>

          <MDBCol lg="8">
            <MDBCard className="mb-4">
               <MDBCardBody>
               <MDBTable>
      <MDBTableHead>
        <tr>
          <th scope='col'>מספר חיסון</th>
          <th scope='col'>יצרן</th>
          <th scope='col'>תאריך</th>
        </tr>
      </MDBTableHead>
      {user === undefined ? <h3>wait...</h3> :
       user.map((f, i) =>
      <MDBTableBody>
     
        <tr>
          <th scope='row'>{f.numVaccination}</th>
          <td>{f.manufacturer}</td>
          <td>{f.dateOfVaccination}</td>
        </tr>
      </MDBTableBody>
      )}

    </MDBTable>

              </MDBCardBody>  
             </MDBCard> 
             <MDBCol md="6">
                <MDBCard className="mb-4 mb-md-0">
                  <MDBCardBody>
                    <p>האם חליתי בעבר?</p>
                  </MDBCardBody>
                </MDBCard>
              </MDBCol>
              <MDBCol md="6">
                <MDBCard className="mb-4 mb-md-0">
                  <MDBCardBody>
                    <MDBBtn outline className="ms-1" onClick={(event) => { saveImge(event) }}>שמור תמונה</MDBBtn>
                  </MDBCardBody>
                </MDBCard>
              </MDBCol>
          </MDBCol>
        </MDBRow>
      </MDBContainer>
    </section>
  );
}}
