  </MDBTableHead>
            <MDBTableBody> 
            {
             
      Basket.map((row)=>
      
      <tr>
        <td><img src={row.img} alt="" className="img-fluid z-depth-0" /></td>,
        <td><h5 className="mt-3"><strong>{row.namePicture}</strong></h5></td>
        <td> <p className="text-muted">{row.price}</p></td>,
        <td>{row.price}</td>
          <td><MDBInput onChange={()=>uppProInB(dispatch,row,Basket)} type="number" default={row.count} className="form-control" style={{ width: "100px" }} /></td>, 
          <td> <strong>{row.price*row.count}</strong></td>,
        <td>
          <MDBTooltip placement="top">
              <MDBBtn color="primary" size="sm" onClick={()=> deletProductInB(dispatch,row)}>
                  X
              </MDBBtn>
              <div>הסר פריט זה מהסל</div>
          </MDBTooltip></td>
        </tr>)
      
   }</MDBTableBody>
   <button onClick={()=>DoInviteByB(dispatch)}>בצע קנייה</button>
          </MDBTable>
        </MDBCardBody>
      </MDBCard>
    </MDBRow> 

  </>
}  
