    //         await setcustomersList(myAddCustomer);

      }, [])
     return<>
     
     <h2>לקוחות</h2>
     
     <MDBTableHead>
        <tr>
          <th>קוד לקוח</th>
          <th>שם</th>
          <th>סיסמא</th>
          <th>מספר אשראי</th>
          
          
        </tr>
      </MDBTableHead>
     
     {

      c != undefined ?
      c.length > 0 &&
      c.map((item) =>
           
      <MDBTable bordered >
      
      <MDBTableBody>
        <tr>
          <td>{item.idCustomer}</td>
          <td>{item.nameCustomer}</td>
          <td>{item.passwordCustomer}</td>
          <td>{item.creditcardCustomer}</td>
        </tr>
      </MDBTableBody>
    </MDBTable>
   
           
      )   
         : <div>אין לקוחות</div>
 
     }
   </>
   
 }
 


    
 

