import produce from 'immer';

const CategoriesInitialState={
    categorie:{},
    categories:[]   
}


export const CategoriesReducer=produce((state,action)=>{
    switch(action.type)
    {
         case "GET_CATEGORIE":state.categorie=action.payload;break;
         case "GET_ALL_CATEGORIES":state.categories.push(action.payload);break;
         case "ADD_CATEGORIE":state.categories=(action.payload);break;
         case "REMOVE_CATEGORIE":state.categories=(action.payload);break;
         case "UPDATE_CATEGORIE":state.categories=(action.payload);break;
    }
},CategoriesInitialState)
