import React, { useEffect } from "react";
import { MDBContainer, MDBRow, MDBCol, MDBBtn, MDBInput } from 'mdbreact';
import { useState } from 'react';
import { useDispatch } from "react-redux";
import { addCompany ,updateCompany } from '../Redux/Companies/CompaniesThunk'
import { MDBModal, MDBModalBody, MDBModalHeader, MDBModalFooter } from 'mdbreact';
import { useSelector } from "react-redux";


export default function SignUp (){
  debugger
  let Company = useSelector((store) => {return store.Companies.company }); 
  debugger
  console.log("Company")
  console.log(Company.codeCompany)
  const [modal14, setModal14] = useState(true);
  const [errors, setErrors] = useState({NameCompany: "שדה חובה", PrivateEmail: "שדה חובה",CustomerServiceEmail: "שדה חובה",CustomerServiceEmailPassword: "שדה חובה", UserName: "שדה חובה", UserPassword: "שדה חובה" });
  const [company, setCompany] = useState({ nameCompany:"",privateEmail:"",customerServiceEmail:"",customerServiceEmailPassword:"",userName:"",userPassword:""});
  const dispatch = useDispatch();

useEffect(async () =>{
if(!Company){
Company.NameCompany="";
Company.PrivateEmail="";
Company.CustomerServiceEmail="";
Company.CustomerServiceEmailPassword="";
Company.UserName="";
Company.UserPassword="";
}
setCompany(Company);
},[])

  const newCompany = async () => {
 debugger
     let NameCompany = document.getElementById("NameCompany").value
     let PrivateEmail = document.getElementById("PrivateEmail").value
     let CustomerServiceEmail = document.getElementById("CustomerServiceEmail").value
     let CustomerServiceEmailPassword = document.getElementById("CustomerServiceEmailPassword").value
     let UserName = document.getElementById("UserName").value
     let UserPassword = document.getElementById("UserPassword").value
     
    if(Company.codeCompany){
      let Customer=null;
      let ProfileCalculations=null;
      const company=await updateCompany(dispatch,Company.codeCompany,NameCompany,PrivateEmail,CustomerServiceEmail,CustomerServiceEmailPassword,UserName,UserPassword,Customer,ProfileCalculations) 
      if(company)
      alert("הפרטים עודכנו בהצלחה");
      setModal14(!modal14);
    }
    else{
      if(errors.errorEmail1===""&&errors.errorEmail2===""&&errors.erroruserName===""&&errors.pass1===""&&errors.pass2===""){
     if (NameCompany&&PrivateEmail&&CustomerServiceEmail&&CustomerServiceEmailPassword&&UserName&&UserPassword) {
      const company = await addCompany(dispatch,NameCompany,PrivateEmail,CustomerServiceEmail,CustomerServiceEmailPassword,UserName,UserPassword);
      alert("ברוך הבא " + UserName + " לאתרינו מקווים שתהיה לך את חווית הגלישה הטובה");
      setModal14(!modal14);
      return company
     }}
      
    else {

      alert("אנא מלא את כל השדות ובדוק שכולם תקינים");

    }}
  }

  const validEmail1 = (event) => {
    var myemail = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;
    if (!event.target.value)
      setErrors({ ...errors, errorEmail1: 'שדה חובה*' })
    else if (event.target.value.match(myemail)) { setErrors({ ...errors, errorEmail1: "" }) }
    else if(!event.target.value.match(myemail)){
      setErrors({ ...errors, errorEmail1: "אימייל לא תקין" })
    }
    setCompany({ ...company, privateEmail: event.target.value })
  }
  

  const validEmail2 = (event) => {
    var myemail = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;
    if (!event.target.value)
      setErrors({ ...errors, errorEmail2: 'שדה חובה*' })
    else if (event.target.value.match(myemail)) { setErrors({ ...errors, errorEmail2: "" }) }
    else if (!event.target.value.match(myemail)) {
      setErrors({ ...errors, errorEmail2: "אימייל לא תקין" })
    }
    setCompany({ ...company, customerServiceEmail: event.target.value })
  }



  //בדיקת תקינות של שם
  const validateName = (event) => {
    let pattern="^(?=[a-zA-Z0-9א-ת ]{4,10}$)(?!.*[_.]{2})[^_.].*[^_.]$";

    
      if (!event.target.value)
        setErrors({ ...errors, erroruserName: "שדה חובה*" });
      else if (!event.target.value.match(pattern)) 
        setErrors({ ...errors, erroruserName: "האורך חייב להיות בין 4 ל10 תווים אין להשתמש ב_ או . " });
      else  if (event.target.value.match(pattern)){
        setErrors({ ...errors, erroruserName: "" });
      
        

      }
      setCompany({ ...company, userName:event.target.value})
    
    }
    
  //בדיקת תקינות של סיסמא
  const validatePass1 = (event) => {

    var passw = /^[A-Za-z]\w{5,20}$/;
    if (!event.target.value)
    setErrors({ ...errors, pass1: 'שדה חובה*' })
   else if (event.target.value.match(passw)) {
      setErrors({ ...errors, pass1: "" });
    }
    else if(!event.target.value.match(passw)) {
      setErrors({ ...errors, pass1: 'סיסמא צריכה להיות בין 5 ל 20 תווים . מכילה רק מספרים ואותיות. אות באנגלית חובה בהתחלה  ' })
    }
    setCompany({ ...company, customerServiceEmailPassword: event.target.value })
  }

  const validatePass2 = (event) => {

    var passw = /^[A-Za-z]\w{5,20}$/;
    if (!event.target.value)
    setErrors({ ...errors, pass2: 'שדה חובה*' })
    else if (event.target.value.match(passw)) {
      setErrors({ ...errors, pass2: "" });
    }

    else if((!event.target.value.match(passw))) {
      setErrors({ ...errors, pass2: 'סיסמא צריכה להיות בין 5 ל 20 תווים . מכילה רק מספרים ואותיות באנגלית אות חובה בהתחלה ' })
    }
    setCompany({ ...company, userPassword: event.target.value })
  }

  const up=(event)=>{
    if (!event.target.value)
    setErrors({ ...errors, nameCompany1: 'שדה חובה*' })
   else setCompany(event.target.value)
  }
   
  
  return (
    <>
      
      <MDBContainer>
        <MDBModal isOpen={modal14} toggle={() => { setModal14(!modal14) }} centered>
          
          <MDBModalHeader toggle={() => { setModal14(!modal14); }}>
            {
              Company.codeCompany?
              <>
            טופס עדכון   
              </>
              :<>טופס הרשמה </>
            }
            </MDBModalHeader>
          <MDBModalBody>
            <MDBContainer>
              <MDBRow>
                <MDBCol md="6">
                  <form>

                    <div className="grey-text">
                      <label>שם חברה</label>
                      <MDBInput  id="NameCompany" value={company.nameCompany} onChange={(e) => { up(e) }} icon="user" group type="text" validate error="wrong"
                        success="right" />
                        <p>{errors.nameCompany1} </p>
                        <label>אימייל פרטי</label>
                      <MDBInput  id="PrivateEmail" value={company.privateEmail} onChange={(e) => { validEmail1(e) }} icon="envelope" group type="email" validate error="wrong"
                        success="right"  /> <p>{errors.errorEmail1}</p>
                         <label>אימייל שירות לקוחות</label>
           
                      <MDBInput id="CustomerServiceEmail" value={company.customerServiceEmail} onChange={(e) => { validEmail2(e) }} icon="envelope" group type="email" validate error="wrong"
                        success="right" /><p>{errors.errorEmail2}</p>
                    
                        <label>סיסמאת אימייל שירות לקוחות</label>
                        <div  className="cm">
                      <MDBInput id="CustomerServiceEmailPassword" value={company.customerServiceEmailPassword} onChange={(e) => { validatePass1(e) }} icon="lock" group type="text" validate />
                     <p>{errors.pass1} </p>
                     </div>
                      <label>שם משתמש</label>
                      <MDBInput  id="UserName" value={company.userName} onChange={(e) => { validateName(e) }} icon="user" group type="text" validate error="wrong" success="right" />
                      <p>{errors.erroruserName}</p>
                      <label>סיסמאת משתמש</label>
                      <MDBInput  id="UserPassword" value={company.userPassword} onChange={(e) => { validatePass2(e) }} icon="lock" group type="text" validate  />
                      <p>{errors.pass2}</p>
                    </div>
                    <div className="text-center">
                    </div>
                  </form>
                </MDBCol>
              </MDBRow>
            </MDBContainer>
          </MDBModalBody>
          <MDBModalFooter>
            {
              Company.codeCompany?
            <MDBBtn color="secondary" onClick={() => newCompany()}>עדכן</MDBBtn>
            :<MDBBtn color="secondary" onClick={() => newCompany()}>הרשם</MDBBtn>
}
          </MDBModalFooter>
        </MDBModal>
      </MDBContainer>
    </>
  );
