import React from "react";
import { useState } from "react/cjs/react.development"

import {
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBCard,
  MDBCardBody,
  MDBModalFooter,
  MDBIcon,
  MDBCardHeader,
  MDBBtn
} from "mdbreact";


const FormPage = (Props) => {
  const [errors, setErrors]=useState({id:"שדה חובה", pass:"שדה חובה"});
    const [customer, setCustomer]=useState(!Props.customer?{}:Props.customer);
    const newCustomer=()=>{
   <FormPage></FormPage>

}

  const validateName=(event)=>{

    if(!event.target.value)
    setErrors({...errors, CustomerName:"שדה חובה"});
    else if (event.target.value.includes(" "))
    setErrors({...errors, CustomerName:"אין לשים רווח"});
else {
    setErrors({...errors, CustomerName:"תקין!"});
}
setUser({...customer, CustomerName: event.target.value })
}

const validatePass=(event)=>
{
    
var passw = /^[A-Za-z]\w{7,14}$/;
if (event.target.value.match(passw)) {
  setErrors({...errors, pass: "תקין!"} );
}
else {
  setErrors({...errors, pass: 'Wrong...!! password password between 7 to 16 characters which contain only characters, numeric digits, underscore and first character must be a letter' })
}
setUser({...customer, CustomerPass: event.target.value })

}


  return (
   
    <MDBContainer>
      <MDBRow>
        <MDBCol md="6">
          <MDBCard>
            <MDBCardBody>
              <MDBCardHeader className="form-header warm-flame-gradient rounded">
                {!(Props.customer.customerPass)?
                <h3 className="my-3">
                  <MDBIcon icon="lock" /> SignUp
                </h3>
                :
                <h3 className="my-3">
                  <MDBIcon icon="lock" /> :טופס עדכון
                </h3>
}
               

              </MDBCardHeader>
              <label
                htmlFor="defaultFormEmailEx"
                className="grey-text font-weight-light"
              >
                Your name
              </label>
              <input
                type="text"
                id="defaultFormNameEx"
                className="form-control"
                value={customer.CustomerName} onChange={(e)=>validateName(e)}  name="CutomerName"
              ></input>
              <p > *{errors.CustomerName}</p>

              <label
                htmlFor="defaultFormPasswordEx"
                className="grey-text font-weight-light"
              >
                Your password
              </label>
              <input
                type="password"
                id="defaultFormPasswordEx"
                className="form-control"
                name="pass" 
                value={customer.CustomerPass}
                 onChange={(e)=>validatePass(e)}
              />
              <p>{errors.pass}</p>


              
              
              <div className="text-center mt-4">
                <MDBBtn color="deep-orange" className="mb-3" type="submit">
                  Login
                </MDBBtn>
              </div>

              <MDBModalFooter>
                <div className="font-weight-light">
                  <p>Not a member? </p>
                  <MDBBtn color="deep-orange" className="mb-3" type="submit" onClick={()=>newCustomer()}>
                  Sign Up
                </MDBBtn>
                  <p>Forgot Password?</p>
                </div>
              </MDBModalFooter>
            </MDBCardBody>
          </MDBCard>
        </MDBCol>
      </MDBRow>
    </MDBContainer>
    
  );
};

export default FormPage;


FormPage.defaultProps={
  Customers:{}
}
