import produce from 'immer';
const CustomersInitialState={
    customer:{},
    customers:[] ,  
    isMenneger:0,
    basket:[]
}


export const CustomersReducer=produce((state,action)=>{
    let i;
    switch(action.type)
    {
        case "GET_CUSROMER":state.customer=action.payload;break;
        case "GET_CUSROMER_ID":state.customer=action.payload;break;
        case "GET_ALL_CUSTOMERS":state.customers.push(action.payload);break;
        case "ADD_CUSTOMER":state.customers.push(action.payload);break;
        case "UPDATE_CUSTOMER":state.customers.push(action.payload);break;
        case "IS_MENNEGER":state.isMenneger=action.payload;break;

//פונקציות של סל קניות
        case "ADD_TO_BASKET":state.basket.push(action.payload);break;//בהתחברות המשתמש
        case "NEW_BASKET":state.basket.length=0;break;//בהתנתקות
        case "UPDATE_PRODUCT_FROM_BASKET":
            for(i=0;i<state.basket.length;i++)
                if(state.basket[i].idProduct==(action.payload).idProduct)break;
                state.basket[i].amount=(action.payload).amount;
                state.basket[i].finalPrice=(action.payload).finalPrice;;break;
        case "DELET_PRODUCT_FROM_BASKET":state.basket=state.basket.filter(x=>x.codePicture!=(action.payload.codePicture));break;


    }
},CustomersInitialState)
