import React from 'react';
import axios from 'axios';
import { useEffect } from 'react';
import {
    MDBCol,
    MDBContainer,
    MDBRow,
    MDBCard,
    MDBCardText,
    MDBCardBody,
    MDBCardImage,
    MDBBtn,
    MDBBreadcrumb,
    MDBBreadcrumbItem,
    MDBProgress,
    MDBProgressBar,
    MDBIcon,
    MDBListGroup,
    MDBListGroupItem,
    MDBFile
} from 'mdb-react-ui-kit';
import { useState } from 'react';
import { getUserFromserverBynameAndPass, getUserDetails, UppdateUser } from '../Redax/Users/UserThank';
import { useDispatch } from 'react-redux';
import { uppdateUser } from '../Redax/Users/UserAction';
import { NavLink } from 'react-router-dom';


export default function Uplo() {

    const [myFile, setMyfile] = useState();




    //העלאת תמונה
    const uploadFile = (e) => {
        setMyfile(e.target.files[0]);

    }
    const saveImge = async (event) => {

        if (myFile == null) {
            alert("יש לבחור קובץ");
        }
        else {
            try {

                //העלאת תמונה לשרת
                const formData = new FormData();
                formData.append('file', myFile, myFile.name);
                await axios.post("https://localhost:44315/upload", formData, { reportProgress: true, observe: 'events' }).
                    then(x => {
                        // console.log(x);

                    })
                }

              catch {

                    }
    
           }
    }
return (

    <>
        <MDBBtn outline className="ms-1"><MDBFile onChange={(event) => { uploadFile(event) }} />העלאת תמונה</MDBBtn>
        <MDBBtn outline className="ms-1" onClick={(event) => { saveImge(event) }}>שמור תמונה</MDBBtn>

    
    </> 
); 
}
