export function GetProduct(product) {
    return { type: "GET_PRODUCT",payload:product }
}
export function GetAllProducts(product) {
    return { type: "GET_ALL_PRODUCTS",payload:product }
}
export function AddProduct(product) {
    return { type: "ADD_PRODUCT",payload:product }
}
export function RemoveProduct(product) {
    return { type: "REMOVE_PRODUCT",payload:product }
}
export function UpdateProduct(product) {
    return { type: "UPDATE_PRODUCT",payload:product }
}
export function GetByCategorie(product) {
    return { type: "GET_BY_CATEGORIE",paylod:product }
}
