import { useEffect } from "react";
import { getAllCustomersFromServer,getCustomerFromserverById,addCustomer } from '../Redux/Customer/CustomersThunk'
import { useState } from 'react';
import { useDispatch } from 'react-redux';

export default function ShowCustomers() {
  let myCustomersFromServer
  let myCustomer,myAddCustomer
  const [customersList,setcustomersList]=useState ([])
  const [oneCustomer,setoneCustomer] = useState({})
  const dispatch = useDispatch();

  useEffect(async () => {
    myCustomersFromServer = await getAllCustomersFromServer(dispatch);
    await console.log(myCustomersFromServer)
    await setcustomersList(myCustomersFromServer)

    const id="1";
    myCustomer = await getCustomerFromserverById(dispatch,id,"611611");
      await setoneCustomer(myCustomer)

      myAddCustomer = await addCustomer(dispatch,5,"sh","711711","322461309");   
            await console.log("הוספת מוצר  "+myAddCustomer);
            await setoneCustomer(myAddCustomer);

      }, [])
     return <>
       <h1>Show customers to manager</h1>
       { <div>
      { customersList.map((x)=>(  
  
              <h3> {x.nameCustomer}  </h3>))
              
     } <h2>oneCustomer: {oneCustomer.nameCustomer}</h2>
    
      </div> }
  </>
}
