import React from 'react';
import { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {MDBIcon, MDBTable, MDBTableHead, MDBTableBody, MDBBtn } from 'mdb-react-ui-kit';
import { Link } from "react-router-dom";
import {getAllCustomersByCompany} from '../Redux/Customers/CustomersThunk'
import {calculationProfile} from '../Redux/ProfileCalculations/ProfileCalculationsThunk'
import { MDBInput } from 'mdbreact';
import {
  BrowserRouter,
  withRouter
} from "react-router-dom"; 
import { useHistory } from 'react-router';
import './myStyle.css'
import { NavLink } from 'react-router-dom';
import { MDBSpinner } from 'mdb-react-ui-kit';

 export default function CalculationCustomers(Props){
  const [show,setShow]=useState(false);
  let Company = useSelector((store) => {return store.Companies.company }); 
  let CodeCalculation = useSelector((store) => {return store.ProfileCalculations.profileCalculation})
  console.log(CodeCalculation)
  const dispatch = useDispatch();
  const [customers, setCustomers] = useState(useSelector((state) => { return state.customers }))
  const [profileCalculation,setProfileCalculation]=useState(!Props.profileCalculation?{}:Props.profileCalculation)
  const [theClassificaion,settheClassificaion]=useState(useSelector((state) => { return state.theClassificaion }))
   const [G,setG]=useState();
  useEffect(async () => {

    
    var date = new Date();
    console.log("date");
    console.log("date111");
    console.log(date);
    var dateNow = ""+date.getDate()+"/"+(date.getMonth()+1)+"/"+date.getFullYear()
    setG(dateNow)
    let C = await getAllCustomersByCompany(dispatch,Company.codeCompany);
  
    if (C) {
        setCustomers(C)
    }
  }, [])


const sendToClassification = async () => {
    debugger
    var date = new Date();
     var dateNow = ""+date.getDate()+"/"+(date.getMonth()+1)+"/"+date.getFullYear()
  
     let DateStart = document.getElementById("DateStart").value
     let DateFinish = document.getElementById("DateFinish").value
    var d1= new Date(DateStart);
    var d2= new Date(DateFinish);
     if(d2.getFullYear()<d1.getFullYear() || d2.getFullYear()>date.getFullYear() || d1.getFullYear()>date.getFullYear() )
      alert("הזן שנה תקינה")
    else{
      if(d2.getFullYear()===d1.getFullYear() )
      {
        if(d2.getMonth()<d1.getMonth()|| d2.getMonth()>date.getMonth() || d1.getMonth()>date.getMonth() )
        alert("הזן חודש תקין")
        else
        {
          if(d2.getMonth()===d1.getMonth())
          {

            if(d2.getDate()<d1.getDate()||d2.getDate()>date.getDate() || d1.getDate()>date.getDate())
            alert("הזן יום תקין")
            else{  if(Company){
              debugger;
            const TheClassificaion = await calculationProfile(dispatch,Company.codeCompany,dateNow,DateStart,DateFinish);
            if(TheClassificaion){
          
             settheClassificaion(TheClassificaion)
          
            // alert("אנא המתן לקוחותיך עוברים סיווג");
            
            return TheClassificaion
          
           
            }}
            else{
              alert("אנא הרשם או התחבר תחילה")
            }}
          }
          else{  if(Company){
            debugger;
          const TheClassificaion = await calculationProfile(dispatch,Company.codeCompany,dateNow,DateStart,DateFinish);
          if(TheClassificaion){
        
           settheClassificaion(TheClassificaion)
        
          alert("אנא המתן לקוחותיך עוברים סיווג");
          
          return TheClassificaion
        
         
          }}
          else{
            alert("אנא הרשם או התחבר תחילה")
          }}
        }
       
      }
     else{
      if(Company){
        debugger;
      const TheClassificaion = await calculationProfile(dispatch,Company.codeCompany,dateNow,DateStart,DateFinish);
      if(TheClassificaion){

       settheClassificaion(TheClassificaion)
       debugger
       setShow(false);

      //  alert("אנא המתן לקוחותיך עוברים סיווג");
      
      return TheClassificaion
    
     
      }}
      else{
        setShow(false);

        alert("אנא הרשם או התחבר תחילה")
      }
    }
    }
  }
  const up=(e)=>{
    setProfileCalculation(e.target.value)
  }
  


 return <>
 {show==false ?
    <BrowserRouter className="table">
    <div className='mysize'></div>
    <h2> סיווג</h2>
   
    <lable className='bDate'>תאריך סיווג נוכחי</lable>
    <p>{G}</p>
     { theClassificaion === undefined ?
     <div className='bDate'>
      
<table>
  <tr>
    <td><label className='bDate'>בחר תאריך התחלה</label></td>
    <td> <lable  className='bDate'>בחר תאריך סיום</lable></td>
    </tr>
  <tr>
    <td> <MDBInput  type="date" id="DateStart" value={profileCalculation.fromDate} onChange={(e) => { up(e) }} style={{width:"150px"}}></MDBInput></td>
    <td> <MDBInput  type="date" id="DateFinish" value={profileCalculation.toDate} onChange={(e) => { up(e) }} style={{width:"150px"}}></MDBInput></td>
    
    </tr>
  </table>
    
   
   
   

    <br/>
    
    <MDBBtn class="btn" onClick={()=>{setShow(true)
sendToClassification()}
}>סווג</MDBBtn>
    </div>
    :
    <p></p>
        }
  
    <p></p>
 
    <div className="square border justify-content-center">
    {theClassificaion === undefined?  <div></div>:
<MDBTable striped>
        <MDBTableHead>
          <tr >
            <th  scope='col'>#</th>
            <th scope='col'>שם:</th>
            <th scope='col'>אימייל:</th>
            <th scope='col'>פרופיל אחרון:</th>
          </tr>
        </MDBTableHead>
        <MDBTableBody>
          {theClassificaion === undefined ? <h6>wait...</h6> :
            theClassificaion.map((f, i) =>

              <tr >
                <th scope='row'>{++i}</th>
                <td >{f.nameCustomer}</td>
                <td>{f.emailCustomer}</td>
                <td>{f.statusCustomer}</td>
                
                < td>
                  <div class="d-grid gap-2 d-md-flex justify-content-md">
                
                    
                  </div>
                </td>
                
              </tr>
            )}
        </MDBTableBody>
      </MDBTable>
 
      }
    </div>
    {theClassificaion === undefined?  <div></div>
  
  :
     <MDBBtn onClick={()=>Props.history.push(`/ChartOfDate/${Company.codeCompany}`)}> צפייה בחישוב אחרון</MDBBtn> 
 }
    <div class="d-flex justify-content-evenly">
    
   
    </div>
 
 
    </BrowserRouter>:
      <>
      <h2>אנא המתן לקוחותיך עוברים סיווג</h2>
        
        

      <MDBSpinner color='success' role='status' size='400px'> 
 </MDBSpinner> 
<br />
 

</>}
  </>
}
