import React, { useEffect } from 'react';
import { MDBBtn, MDBCard, MDBCardBody, MDBCardImage, MDBCardTitle, MDBCardText, MDBCol, } from 'mdbreact';
import { useDispatch,useSelector } from 'react-redux';
import { getProductById } from '../Redux/Product/ProductsThunk';
import { useParams } from 'react-router';
import { useState } from 'react';
import { MDBNavbarLink } from 'mdb-react-ui-kit';
import {AddToBasket} from '../Redux/Customer/CustomerAction'

const  CardExample =(code) => {
   const params=useParams;
  const dispatch=useDispatch();
  const M = useSelector((store) => { return store.customers.isMenneger });
  const U = useSelector((store) => {return store.customers.customer });

   let c=code.match.params.id;
   let [p,setP]=useState({});

   useEffect(async () => {
   let p1=await getProductById(dispatch,c); 
    setP(p1);
  }, [])
 
  const addToB = (x) => {
    if(U.passwordUser =="")
    {
      debugger;
      alert("על מנת להוסיף פרטים עליך להירשם או להיכנס אם יש לך חשבון כבר");
    globalThis.location.href = 'http://localhost:3000/sign_in';
  }
  else
  {
    const pToBasket={
      codePicture:x.codePicture,
      namePicture:x.namePicture,
      count:1,
      price:x.price,
      finalPrice:x.price
    }
    dispatch(AddToBasket(pToBasket));
     alert("המוצר נוסף בהצלחה");
  }
   
  
}
   
 
  return (
    <MDBCol>
      <MDBCard style={pStyles}>
        <MDBCardImage className="img-fluid" src={`http://localhost:10362/Images/${p.imageProduct}`} />
        <MDBCardBody>
          <MDBCardTitle>{p.nameProduct}</MDBCardTitle>
          <MDBCardText>
          מידה:{p.sizeProduct},
          כמות במלאי:{p.amountProduct},
          מחיר:{p.priceProduct} ש"ח 
          
          </MDBCardText>
          <MDBNavbarLink><MDBBtn onClick={()=>addToB(p)} >הוסף לסל</MDBBtn></MDBNavbarLink>
        </MDBCardBody>
      </MDBCard>
    </MDBCol>
  )
}

export default CardExample;
const pStyles = {
  float:"right",
      margin:"auto",
      with:"100px",
      margin:'20px 3%'
      
  }
