import React from 'react';
import {useDispatch} from 'react-redux';
import { MDBBtn, MDBCard, MDBCardBody, MDBCardImage, MDBCardTitle, MDBCardText, MDBCol } from 'mdbreact';
import { useParams } from 'react-router';
import { getProductById } from '../Redux/Product/ProductsThunk';
export default async function  CardExample (id)  {
  let params = useParams();
  const dispatch = useDispatch();
  const [p, setP] = useState([]);
  let product = getProductById(dispatch, params.idProduct);
  await setP(product);
  return (
    <MDBCol>
      <MDBCard style={{ width: "22rem" }}>
        <MDBCardImage className="img-fluid" src='http://localhost:10362/Images/${Props.imageProduct}' waves />
        <MDBCardBody>
          <MDBCardTitle> p.nameProduct</MDBCardTitle>
          <MDBCardText>
            Some quick example text to build on the card title and make
            up the bulk of the card&apos;s content.
          </MDBCardText>
          <MDBBtn href="#">הוסף לסל</MDBBtn>
        </MDBCardBody>
      </MDBCard>
    </MDBCol>
  )
}
