import React from "react";
import { MDBContainer, MDBRow, MDBCol, MDBBtn } from 'mdbreact';
 import  {useState} from 'react';
 import { useDispatch } from "react-redux";
 import {updateProduct,getProductById} from '../Redux/Product/ProductsThunk'
 import { useParams } from "react-router";
 import { useEffect } from "react";

const ProductUpdate = (code) => {
    const dispatch=useDispatch();
    let c=code.match.params.id;
    
    let [p,setP]=useState({});

   useEffect(async () => {
    let p1=await getProductById(dispatch,c); 
    console.log(p1);
     setP(p1);
   }, [])

return (
<MDBContainer>
  <MDBRow>
    <MDBCol md="6">
      <form>
        <p className="h4 text-center mb-4">הוספת מוצר</p>
        <label htmlFor="defaultFormRegisterNameEx" className="grey-text">
          Product id
        </label>
        <input type="text" id="i9" value={""}  className="form-control" />
    
        <br />
        <label htmlFor="defaultFormRegisteridEx" className="grey-text">
        Product name
        </label>
        <input type="text" id="i10" value={""} className="form-control" />
        <br />
        <label htmlFor="defaultFormRegistercreaditCardEx" className="grey-text">
          Product Id Of Categorie
        </label>
        <input type="text" id="i11" value={""} className="form-control" />
        <br />
        <label htmlFor="defaultFormRegisterPasswordEx" className="grey-text">
          Product Price
        </label>
        <input type="text" id="i12" value={""} className="form-control" />
        <div className="text-center mt-4"></div>
        <br />
        <label htmlFor="defaultFormRegisterPasswordEx" className="grey-text">
          Product Image
        </label>
        <input type="text" id="i5" value={""} className="form-control" />
        <div className="text-center mt-4">
        <br />
        <label htmlFor="defaultFormRegistercreaditCardEx" className="grey-text">
        Product Size
        </label>
        <input type="text" id="i6" value={""} className="form-control" />
        <br />
        <label htmlFor="defaultFormRegistercreaditCardEx" className="grey-text">
        Product Amount 
        </label>
        <input type="text" id="i7" value={""} className="form-control" />

        
          <MDBBtn color="deep-orange" className="mb-3" type="submit" onClick={() => newProducts(dispatch)}>
          הוסף מוצר
                </MDBBtn>
        </div>
      </form>
    </MDBCol>
  </MDBRow>
</MDBContainer>
);
};

export default ProductUpdate;
