import React from "react";
import { MDBContainer, MDBRow, MDBCol, MDBBtn, MDBInput } from 'mdbreact';
import { getCustomerFromserverById } from '../Redux/Customer/CustomersThunk';
import FormPage from './SignUp'

const IsMember = async(dispatch) => {
  const id = document.getElementById("id").value;
  const pass = document.getElementById("password").value;
  const customer =  await getCustomerFromserverById(dispatch,id, pass);
  // console.log(user);
//למה הוא מדפיס גם את האלס ??????????????
if (customer) {
  // console.log(user);
  // <ShowProducts></ShowProducts>
  //איך אפשר להביא רקקקק את הפונקצייה הזו בלי כל השאר?????????????
  globalThis.location.href='http://localhost:3000/';
return customer;
}
else
 {
   alert("אחד או יותר מהנתונים שגוי...");
  
  return;
}
}


const SignIn = () => {
  const dispatch=useDispatch();

  return (
    <Route>
      <div>
        <Switch>

          <Route path='./SignUp' exact component={FormPage} />
          <Redirect from="/" to="/home" exact />

        </Switch>
       
        <MDBContainer>
          <MDBRow>
            <MDBCol md="6">
               
              <form>
                <p className="h5 text-center mb-4">SignIn</p>
                <div className="grey-text">
                  <MDBInput label="Your name" icon="user" group type="text" validate error="wrong"
                    success="right" id="name" />

                  <MDBInput label="Your password" icon="lock" group type="password" validate id="pass" />
                </div>


                <div className="text-center">
                 
      
<MDBBtn color="primary"  onClick={()=>IsMember(dispatch)}>כניסה</MDBBtn> 
              
                </div>
              </form>
            </MDBCol>
          </MDBRow>
        </MDBContainer>
      </div>
    </Route>
  );
};


export default SignIn;
