import React, { Component, useState } from 'react';
import {
  MDBContainer,
  MDBNavbar,
  MDBNavbarBrand,
  MDBNavbarToggler,
  MDBIcon,
  MDBNavbarNav,
  MDBNavbarItem,

  MDBNavbarLink,
  MDBBtn,
  MDBDropdown,
  MDBDropdownToggle,
  MDBDropdownMenu,
  MDBDropdownItem,
  MDBDropdownLink,
  MDBCollapse
} from 'mdb-react-ui-kit';
import {
  BrowserRouter,
  Switch,
  Route,
  Link,
  NavLink,
  
} from "react-router-dom";
  
  import ShowProducts from './showProducts';
  import ShowCustomers from './showCustomers';
  import ShowInvities from './showInvities'
  import FormPage from './SignUp'
  import ShowProductsByCategory from './showProductsByCategory';
 
  class TheNav extends React.Component {
    render(){
    return (
      <BrowserRouter>
      <div>
      {<Nav></Nav> }
         <Switch>
         <Route path='/Home' exact component={TheNav }></Route>
            <Route path='/showProducts' exact component={ShowProducts }></Route>
            <Route path='/SignUp' exact component={FormPage} ></Route> 
            {/* <Route path='/SignIn' exact component={SignIn} ></Route>  */}
            <Route path='/ShowProductsByCategory/:categorie' exact component={ShowProductsByCategory} ></Route> 
          </Switch>    
      </div>
      </BrowserRouter>
    );
  }}
  



function Nav() {
  const [showBasic, setShowBasic] = useState(false);

  return (
    <header style={{ direction: 'rtl', fontWeight: 'bold', fontSize: '20px' }}>
      <MDBNavbar expand='lg' light bgColor='white'>
        <MDBContainer fluid>
          <MDBNavbarToggler
            aria-controls='navbarExample01'
            aria-expanded='false'
            aria-label='Toggle navigation'
          >
            <MDBIcon fas icon='bars' />
          </MDBNavbarToggler>

          <div className='collapse navbar-collapse' id='navbarExample01'>
            <MDBNavbarNav right className='mb-2 mb-lg-0'>
            <MDBNavbarNav right>
            <MDBNavbarBrand>
          <strong className="white-text"><MDBIcon className='ms-1' icon="object-group" size='2x' /></strong>
        </MDBNavbarBrand>
        
              <MDBNavbarItem active>
              <MDBNavbarLink>  <NavLink aria-current='page' to='/home'>  דף הביית </NavLink></MDBNavbarLink>
              </MDBNavbarItem>
              <MDBNavbarItem>
              <MDBNavbarLink>  <NavLink to='/products' >צפה בכל המוצרים</NavLink></MDBNavbarLink>
              </MDBNavbarItem>
              <MDBNavbarItem>
              <MDBNavbarLink>  <NavLink to='/category' >צפה במוצרים לפי קטגוריות</NavLink></MDBNavbarLink>
              </MDBNavbarItem>
              <MDBNavbarItem>
              <MDBNavbarLink>  <NavLink to='/basket' >סל הקניות שלי</NavLink></MDBNavbarLink>
              </MDBNavbarItem>
              </MDBNavbarNav>
              <MDBNavItem>
              <MDBDropdown>
                <MDBDropdownToggle nav caret>
                  <MDBIcon icon="user" />
                </MDBDropdownToggle>
                <MDBDropdownMenu className="dropdown-default">
                  <NavLink to='/sign_in'><MDBDropdownItem>התחבר</MDBDropdownItem></NavLink>
                 <NavLink to='/menneger' > <MDBDropdownItem >היכנס כמנהל</MDBDropdownItem></NavLink>
                 {(M == 1)&&
                 <button onClick={()=>{(dispatch(isMenneger(0))),alert("מצב מנהל כבוי")}}> <MDBDropdownItem>צא ממצב מנהל</MDBDropdownItem></button>
                 }
                  <NavLink to='/sign'><MDBDropdownItem>הרשם עכשיו</MDBDropdownItem></NavLink>
                  {(U.passwordUser !="")&&
                 <NavLink to='/' > <MDBDropdownItem href="#!">עדכן פרטיך אצלנו</MDBDropdownItem></NavLink>
                  }
                </MDBDropdownMenu>
              </MDBDropdown>
            </MDBNavItem>
             
            </MDBNavbarNav>
          </div>
        </MDBContainer>
      </MDBNavbar>

      <div
        className='p-5 text-center bg-image'
        style={{ backgroundImage: "url('https://mdbcdn.b-cdn.net/img/new/slides/041.jpg')", height: 400 }}
      >
        <div className='mask' style={{ backgroundColor: 'rgba(0, 0, 0, 0.6)' }}>
          <div className='d-flex justify-content-center align-items-center h-100'>
            <div className='text-white'>
              <h1 className='mb-3'>THE GALERY</h1>
              {(U.passwordUser =="")&&
              <div>
