import React from "react";
import { Pie } from "react-chartjs-2";
import { MDBContainer } from "mdbreact";

class LittleChartsPage extends React.Component {
  state = {
    dataPie: {
      labels: ["ביקורות חיוביות", "ביקורות שליליות"],
      datasets: [
        {
          data: [300, 250],
          backgroundColor: [
            "pink",
            "#46BFBD",
            "#FDB45C",
            "#949FB1",
            "#4D5360",
            "#AC64AD"
          ],
          hoverBackgroundColor: [
            "gray",
            "#5AD3D1",
            "#FFC870",
            "#A8B3C5",
            "#616774",
            "#DA92DB"
          ]
        }
      ]
    }
  }

  render() {
    return (
      <MDBContainer>
        <h3 className="mt-5">תרשים סיווג נוכחי</h3>
        <Pie data={this.state.dataPie} options={{ responsive: true }} />
      </MDBContainer>
    );
  }
}

export default LittleChartsPage;
