import React, { Component, useState } from 'react';
import {  MDBRow, MDBCol, MDBInput } from 'mdbreact';
import { MDBContainer, MDBBtn, MDBModal, MDBModalBody, MDBModalHeader, MDBModalFooter } from 'mdbreact';
import { useDispatch } from 'react-redux';
import {getCompanyByNameAndPassword} from '../Redux/Companies/CompaniesThunk'
import { useHistory } from 'react-router';
import {
  BrowserRouter,
  Switch,
  Route,
  Link,
  NavLink,
  withRouter
} from "react-router-dom";




 export default function SignIn(){
  const [modal14,setModal14]=useState(true);
  const history=useHistory();
  const dispatch=useDispatch();
  
  const ModalPage = async() => {  
    const PrivateEmail = document.getElementById("PrivateEmail").value;
    const UserName = document.getElementById("UserName").value;
    const Password = document.getElementById("Password").value;
    const company =  await  getCompanyByNameAndPassword(dispatch,PrivateEmail,UserName, Password);
    
  if (company) 
  {
    alert("ברוך הבא "+UserName);
     setModal14(!modal14);
  return company;
  }
  else
   {
     alert("אחד או יותר מהנתונים שגוי...");
    
    return;
  }
  
  }

  return (
    <BrowserRouter>
      <MDBContainer>
        <MDBModal isOpen={modal14} toggle={()=>{setModal14(!modal14)}} centered>
          <MDBModalHeader toggle={()=>{setModal14(!modal14);}}>Sign In</MDBModalHeader>
          <MDBModalBody>
          <MDBContainer>
  <MDBRow>
    <MDBCol md="6">
      <form>
       
        <div className="grey-text">
        <MDBInput label="Email" id="PrivateEmail" icon="envelope" group type="text" validate error="wrong"
            success="right" />
          <MDBInput label="User name" id="UserName" icon="user" group type="text" validate error="wrong"
            success="right" />
          <MDBInput label="Password" id="Password" icon="lock" group type="password" validate />
        </div>
        <div className="text-center">
        </div>
      </form>
    </MDBCol>
  </MDBRow>
</MDBContainer>
          </MDBModalBody>
          <MDBModalFooter>
            <MDBBtn color="secondary" onClick={()=>ModalPage(dispatch)}>SignIn</MDBBtn>
            <NavLink to={`/SignUp`}> SignUp</NavLink>
           
          </MDBModalFooter>
        </MDBModal>
      </MDBContainer>
      </BrowserRouter>
    );
  }
