import React from "react";
import { MDBContainer, MDBRow, MDBCol, MDBBtn } from 'mdbreact';
 import  {useState} from 'react';
 import { useDispatch } from "react-redux";
import {getCategorieById,apdateCategorie} from '../Redux/Categorie/CategoriesThunk'
 import { useParams } from "react-router";
 import { useEffect } from "react";
 import { useSelector } from "react-redux";

const CategorieUpdate = (code) => {
    
    const dispatch=useDispatch();
    let c=code.match.params.id;
   
    
    let [c2,setC2]=useState({});

   useEffect(async () => {
  
     let c1=await getCategorieById(dispatch,c); 
    
    setC2(c1);
   }, [])

   const newCategorie=async(dispatch)=>{
    
    let IdCategories=document.getElementById("cc1").value
    let NameCategories=document.getElementById("cc2").value
   

   if(IdCategories)
   {
   
    
    const categorie=await apdateCategorie(dispatch,IdCategories,NameCategories);
   
    alert("הפרטים עודכן בהצלחה");
     return categorie
   }
   else
   {
  
    alert("אחד מהפרטים אינו נכון אנא מלא בשנית")
    
  }
  

  }
const up=(e)=>{
    setC2(e.target.value)
}


return (
<MDBContainer>
  <MDBRow>
    <MDBCol md="6">
      <form>
        <p className="h4 text-center mb-4">עדכון קטגוריה</p>
        <label htmlFor="defaultFormRegisterNameEx" className="grey-text">
          Categorie id
        </label>
        <input type="text" id="c1" value={c2.IdProduct}  className="form-control" id="cc1" onChange={(e)=>up(e)}/>
    
        <br />
        <label htmlFor="defaultFormRegisteridEx" className="grey-text">
        Categorie name
        </label>
        <input type="text" id="c2" value={c2.NameProduct} className="form-control" id="cc2" onChange={(e)=>up(e)}/>
        <br />
        <div className="text-center mt-4">
        
          <MDBBtn color="deep-orange" className="mb-3" type="submit" onClick={() => newCategorie(dispatch)}>
          עדכון קטגוריה
                </MDBBtn>
        </div>
      </form>
    </MDBCol>
  </MDBRow>
</MDBContainer>
);
};

export default CategorieUpdate;
