import axios from "axios"
import { useDispatch } from "react-redux";
import {GetAllCustomersOfCompanies,GetAllCustomersByCompany,GetCustomerByCode,AddCustomer,UpdateCustomer,RemoveCustomer } from "./CustomersAction";
export const CustomersURL="http://localhost:21507/api/Customers/"

 

 
export const getAllCustomersOfCompanies = async (dispatch) => {
    const customersPromise = axios.get(CustomersURL+"GetAllCustomersOfCompanies");
    const response = await customersPromise;
    const customers = response.data;
    dispatch(GetAllCustomersOfCompanies(customers))
    return customers;

}
export const getAllCustomersByCompany = async (dispatch,codeCompany) => {
    const customersPromise = axios.get(CustomersURL+"GetAllCustomersByCompany/"+codeCompany);
    const response = await customersPromise;
    const customers = response.data;
    dispatch(GetAllCustomersByCompany(customers))
    return customers;

}
export const getCustomerByCode = async (dispatch,codeCustomer) => {
    const customerPromise = axios.get(CustomersURL+"GetCustomerByCode/"+codeCustomer);
    const response = await customerPromise;
    const customer = response.data;
    dispatch(GetCustomerByCode(customer))
    return customer;

}
export const addCustomer=async(dispatch,codeCompany,nameCustomer,emailCustomer,profileCustomer,calculationCode)=>
  {   
       try{
           
       const newCustomer={ codeCompany:codeCompany,nameCustomer: nameCustomer,emailCustomer:emailCustomer,profileCustomer:profileCustomer,calculationCode:calculationCode};
       const customerPromise =await axios.post(CustomersURL+"AddCompany",newCustomer);
       await  dispatch(AddCustomer(customerPromise.data));
       return customerPromise.data;
       }
       catch(e){
           console.log(e);
       }
       
    }


    export const updateCustomer = async (dispatch,codeCompany,nameCustomer,emailCustomer,profileCustomer,calculationCode) => {
        const updateCustomer  = { codeCompany:codeCompany,nameCustomer: nameCustomer,emailCustomer:emailCustomer,profileCustomer:profileCustomer,calculationCode:calculationCode};
        try {
            const customerPromise = await axios.put(CompaniesURL + "/UpdateCustomer/", updateCustomer);
            await dispatch(UpdateCustomer(customerPromise.data));
            const response = await customerPromise;
            return customerPromise.data;
        }
        catch (e) {
            console.log(e);
        }
    }

