        try {
            const companyPromise = await axios.delete(CompaniesURL + "/RemoveCompany?Id=" + CodeCustomer);
            await dispatch(RemoveCompany(companyPromise));
            const response = await companyPromise.data;
            return companyPromise.data;
        }
