import React, { Component, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {
  MDBContainer,
  MDBNavbar,
  MDBNavbarBrand,
  MDBNavbarToggler,
  MDBIcon,
  MDBNavbarNav,
  MDBNavbarItem,

  MDBNavbarLink,
  MDBBtn,
  MDBDropdown,
  MDBDropdownToggle,
  MDBDropdownMenu,
  MDBDropdownItem,
  MDBDropdownLink,
  MDBCollapse
} from 'mdb-react-ui-kit';
import {
  BrowserRouter,
  Switch,
  Route,
  Link,
  NavLink,
} from "react-router-dom";
 import SignIn from './SignIn';

class TheNav extends React.Component {
    render(){
    return (
      <BrowserRouter>
      <div>
      {<Nav></Nav> }
         <Switch>
          <Route path='/Home' exact component={TheNav }></Route>
            <Route path='/SighIn' exact component={SignIn}></Route>
             {/* <Route path='/ShowProductsByCategory/:categorie' exact component={ShowProductsByCategory} ></Route>   */}
          </Switch>    
      </div>
      </BrowserRouter>
    );
  }}
  



function Nav() {
 
  const [showBasic, setShowBasic] = useState(false);
   const dispatch = useDispatch();
//    const M = useSelector((store) => {return store.customers.isMenneger });
//    let C = useSelector((store) => {return store.customers.customer });
  
   

  return (
    
    <header style={{ fontWeight:'bold'}}>
    <MDBNavbar expand='lg' light bgColor='light'>
      <MDBContainer fluid>
        <NavLink to="http://localhost:3000/">דף הבית</NavLink>

        <MDBNavbarToggler
          aria-controls='navbarSupportedContent'
          aria-expanded='false'
          aria-label='Toggle navigation'
          onClick={() => setShowBasic(!showBasic)}
        >
          <MDBIcon icon='bars' fas />
        </MDBNavbarToggler>

        <MDBCollapse navbar show={showBasic}>
          <MDBNavbarNav className='mr-auto mb-2 mb-lg-0'>
            
            <MDBNavbarItem>
              <MDBDropdown>
                <MDBDropdownToggle tag='a' className='nav-link'>
                  משתמש
                </MDBDropdownToggle>
                <MDBDropdownMenu>
                  <MDBDropdownItem>
                    <NavLink to="/SinInMeneger">הכנס כמנהל</NavLink>
                  </MDBDropdownItem>
                  <MDBDropdownItem>
                    <NavLink to="/SighIn">הכנס כמשתמש</NavLink>
                  </MDBDropdownItem>
                </MDBDropdownMenu>
              </MDBDropdown>
            </MDBNavbarItem>
              <MDBNavbarItem>
            
            </MDBNavbarItem>
          </MDBNavbarNav>

          <form className='d-flex input-group w-auto'>
            <input type='search' className='form-control' placeholder='Type query' aria-label='Search' />
            <MDBBtn color='primary'>חפש</MDBBtn>
          </form>
        </MDBCollapse>
      </MDBContainer>
    </MDBNavbar>
    <div
        className='p-5 text-center bg-image'
        style={{  height: 500 }}
      >
       
     
    <div className='mask' style={{ backgroundColor: 'rgba(0, 0, 0, 0.6)' }}>
          <div className='d-flex justify-content-center align-items-center h-100'>
            <div className='text-white'>
              <h1 className='mb-3'></h1>
             
              
            </div>
          </div>
        </div>  
        </div>
        
    </header>
    
    
  );
  
}
export default TheNav;
