import React from "react";
import { MDBContainer, MDBRow, MDBCol, MDBBtn } from 'mdbreact';
 import  {useState} from 'react';
 import { useDispatch } from "react-redux";
 import {addProduct} from '../Redux/Product/ProductsThunk'

const ProductUpdate = (Props) => {
  const dispatch = useDispatch();
  const [errors, setErrors]=useState({id:"שדה חובה", pass:"שדה חובה"});
   const [product, setProduct]=useState(!Props.product?{}:Props.product);

   const newProducts=async(dispatch)=>{
     debugger;
     let IdProduct=document.getElementById("i9").value
     let NameProduct=document.getElementById("i10").value
     let IdCategorie=document.getElementById("i11").value
     let PriceProduct=document.getElementById("i12").value
     let ImageProduct=document.getElementById("i5").value
     let SizeProduct=document.getElementById("i6").value
     let AmountProduct=document.getElementById("i7").value
    if(IdProduct)
    {
    
     
     const product=await addProduct(dispatch,IdProduct,NameProduct ,IdCategorie,PriceProduct,ImageProduct,SizeProduct,AmountProduct);
     alert("wellcome"+product.NameProduct);
      return product
    }
    else
    {
   
     alert("קוד מוצר הינו שדה חובה להוספת מוצר")
     
   }
   
 
   }
   //בדיקת תקינות של שם
   



return (
<MDBContainer>
  <MDBRow>
    <MDBCol md="6">
      <form>
        <p className="h4 text-center mb-4">הוספת מוצר</p>
        <label htmlFor="defaultFormRegisterNameEx" className="grey-text">
          Product id
        </label>
        <input type="text" id="i9" value={product.IdProduct}  className="form-control" />
    
        <br />
        <label htmlFor="defaultFormRegisteridEx" className="grey-text">
        Product name
        </label>
        <input type="text" id="i10" value={product.NameProduct} className="form-control" />
        <br />
        <label htmlFor="defaultFormRegistercreaditCardEx" className="grey-text">
          Product Id Of Categorie
        </label>
        <input type="text" id="i11" value={product.IdCategorie} className="form-control" />
        <br />
        <label htmlFor="defaultFormRegisterPasswordEx" className="grey-text">
          Product Price
        </label>
        <input type="text" id="i12" value={product.PriceProduct} className="form-control" />
        <div className="text-center mt-4"></div>
        <br />
        <label htmlFor="defaultFormRegisterPasswordEx" className="grey-text">
          Product Image
        </label>
        <input type="text" id="i5" value={product.ImageProduct} className="form-control" />
        <div className="text-center mt-4">
        <br />
        <label htmlFor="defaultFormRegistercreaditCardEx" className="grey-text">
        Product Size
        </label>
        <input type="text" id="i6" value={product.SizeProduct} className="form-control" />
        <br />
        <label htmlFor="defaultFormRegistercreaditCardEx" className="grey-text">
        Product Amount 
        </label>
        <input type="text" id="i7" value={product.AmountProduct} className="form-control" />

        
          <MDBBtn color="deep-orange" className="mb-3" type="submit" onClick={() => newProducts(dispatch)}>
          הוסף מוצר
                </MDBBtn>
        </div>
      </form>
    </MDBCol>
  </MDBRow>
</MDBContainer>
);
};

export default ProductUpdate;
