// }
// setUser({...customer, CustomerPass: event.target.value })

// }


  return (
   
    <MDBContainer>
      <MDBRow>
        <MDBCol md="6">
          <MDBCard>
            <MDBCardBody>
              <MDBCardHeader className="form-header warm-flame-gradient rounded">
                {!(Props.Customer.CustomerPass)?
                <h3 className="my-3">
                  <MDBIcon icon="lock" /> SignUp
                </h3>
                :
                <h3 className="my-3">
                  <MDBIcon icon="lock" /> :טופס עדכון
                </h3>
}
               

              </MDBCardHeader>
              <label
                htmlFor="defaultFormEmailEx"
                className="grey-text font-weight-light"
              >
                Your name
              </label>
              <input
                type="text"
                id="defaultFormNameEx"
                className="form-control"
                value={customer.CustomerName} onChange={(e)=>validateName(e)}  name="CutomerName"
              ></input>
              <p > *{errors.CustomerName}</p>

              <label
                htmlFor="defaultFormPasswordEx"
                className="grey-text font-weight-light"
              >
                Your password
              </label>
              <input
                type="password"
                id="defaultFormPasswordEx"
                className="form-control"
                name="pass" 
                value={customer.CustomerPass}
                 onChange={(e)=>validatePass(e)}
              />
              <p>{errors.pass}</p>


              
              
              <div className="text-center mt-4">
                <MDBBtn color="deep-orange" className="mb-3" type="submit">
                  Login
                </MDBBtn>
              </div>

              <MDBModalFooter>
                <div className="font-weight-light">
                  <p>Not a member? </p>
                  <MDBBtn color="deep-orange" className="mb-3" type="submit" onClick={()=>newCustomer()}>
                  Sign Up
                </MDBBtn>
                  <p>Forgot Password?</p>
                </div>
              </MDBModalFooter>
            </MDBCardBody>
          </MDBCard>
        </MDBCol>
      </MDBRow>
    </MDBContainer>
    
  );
};

export default FormPage;


