import React from 'react';
import { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { MDBTable, MDBTableHead, MDBTableBody, MDBBtn } from 'mdb-react-ui-kit';
import { Link } from "react-router-dom";

import {getAllCustomersByCompany} from '../Redux/Customers/CustomersThunk'
import { MDBInput } from 'mdbreact';

const MyCustomers = () => {
  let Company = useSelector((store) => {return store.Companies.company }); 
  console.log(Company)
  const dispatch = useDispatch();
  const [customers, setCustomers] = useState(useSelector((state) => { return state.customers }))

  useEffect(async () => {
    let C = await getAllCustomersByCompany(dispatch,Company.codeCompany);
    await console.log("C");
    await console.log(C);
    if (C) {
        setCustomers(C)
    }
  }, [])


const newCompany = async () => {
  
     let NameCompany = document.getElementById("NameCompany").value
     let PrivateEmail = document.getElementById("PrivateEmail").value
     let CustomerServiceEmail = document.getElementById("CustomerServiceEmail").value
     let CustomerServiceEmailPassword = document.getElementById("CustomerServiceEmailPassword").value
     let UserName = document.getElementById("UserName").value
     let UserPassword = document.getElementById("UserPassword").value
     
    if(Company){
      let Customer=null;
      let ProfileCalculations=null;
      const company=await updateCompany(dispatch,Company.codeCompany,NameCompany,PrivateEmail,CustomerServiceEmail,CustomerServiceEmailPassword,UserName,UserPassword,Customer,ProfileCalculations) 
      if(company)
      alert("הפרטים עודכנו בהצלחה");
      setModal14(!modal14);
    }

    else{
    if (PrivateEmail && UserName && UserPassword) {
      const company = await addCompany(dispatch,NameCompany,PrivateEmail,CustomerServiceEmail,CustomerServiceEmailPassword,UserName,UserPassword);
      alert("ברוך הבא " + UserName + " לאתרינו מקווים שתהיה לך את חווית הגלישה הטובה");
      setModal14(!modal14);
      return company
    }
    else {

      alert("אנא מלא את כל השדות ");

    }}
  }
  








 return <>
    
    <h1>הלקוחות שלי</h1>
    <label>בחר תאריך התחלה</label>
    <MDBInput type="date" id="DateStart" style={{width:"150px"}}></MDBInput>
    <lable>בחר תאריך סיום</lable>
    <MDBInput type="date" id="DateFinish" style={{width:"150px"}}></MDBInput>
    <Link to="Classification"><MDBBtn href='#'>סווג</MDBBtn></Link>
    <p></p>
 
    <div className="square border justify-content-center">
      <MDBTable striped>
        <MDBTableHead>
          <tr>
            <th scope='col'>#</th>
            <th scope='col'>שם:</th>
            <th scope='col'>אימייל:</th>
            <th scope='col'>פרופיל אחרון:</th>
          </tr>
        </MDBTableHead>
        <MDBTableBody>
          {customers === undefined ? <h6>wait...</h6> :
            customers.map((f, i) =>

              <tr>
                <th scope='row'>{++i}</th>
                <td>{f.nameCustomer}</td>
                <td>{f.emailCustomer}</td>
                <td>{f.ProfileCustomer}</td>
                
                < td>
                  <div class="d-grid gap-2 d-md-flex justify-content-md">
                
                    
                  </div>
                </td>
                
              </tr>
            )}
        </MDBTableBody>
      </MDBTable>
      
    </div>
    
    <div class="d-flex justify-content-evenly">
    
   
    </div>
  </>
}
export default MyCustomers;
