import axios from "axios"
import { useDispatch } from "react-redux";
import { GetCompanyByNameAndPassword,AddCompany,GetAllCompanies,UpdateCompany,RemoveCompany } from "./CompaniesAction";
export const CompaniesURL="http://localhost:21507/api/Companies/"
import { GetCompanyByCode } from "./CompaniesAction";
 
export const getAllCopmanies=async(dispatch)=>
{
    const companyPromise=axios.get(CompaniesURL+"GetAllCompanies");
     const response=await companyPromise;
     const companies=response.data;
     dispatch(GetAllCompanies(companies))
     return companies;
 }
 
 
export const getCompanyByNameAndPassword = async (dispatch,email,name,password) => {
    const companyPromise = axios.get(CompaniesURL+"GetCompanyByNameAndPassword/"+email+"%2C%D7%90%D7%95%D7%A9%D7%A8%20%D7%A2%D7%93%2C"+password+"?PrivateEmail="+email+"&UserName="+name+"&UserPassword="+password);
    const response = await companyPromise;
    const company = response.data;
    dispatch(GetCompanyByNameAndPassword(company))
    return company;

}
export const getCompanyByCode = async (dispatch,code) => {
    
    const companyPromise = axios.get(CompaniesURL+"GetCompanyByCode/"+code);
    const response = await companyPromise;
    const company = response.data;
    console.log(company);
    dispatch(GetCompanyByCode(company))
    return company;

}
export const addCompany=async(dispatch,nameCompany,privateEmail,customerServiceEmail,customerServiceEmailPassword,userName,userPassword)=>
  {   
       try{
           
       const newCompany={ nameCompany:nameCompany,privateEmail: privateEmail,customerServiceEmail:customerServiceEmail,customerServiceEmailPassword:customerServiceEmailPassword,userName:userName,userPassword:userPassword};
       const companyPromise =await axios.post(CompaniesURL+"AddCompany",newCompany);
       console.log(companyPromise);
       await  dispatch(AddCompany(companyPromise.data));
       return companyPromise.data;
       }
       catch(e){
           console.log(e);
       }
       
    }

    export const removeCompany = async (dispatch, CodeCustomer) => {
        try {
            const companyPromise = await axios.delete(CompaniesURL + "RemoveCompany?Id=" + CodeCustomer);
            await dispatch(RemoveCompany(companyPromise));
            const response = await companyPromise.data;
            return companyPromise.data;
        }
        catch (e) {
            console.log(e);
        }
    }

    export const updateCompany = async (dispatch,codeCompany,nameCompany,privateEmail,customerServiceEmail,customerServiceEmailPassword,userName,userPassword,customer,profileCalculations) => {
        const updateCompany  = {codeCompany:codeCompany,nameCompany:nameCompany,privateEmail:privateEmail,customerServiceEmail:customerServiceEmail,customerServiceEmailPassword:customerServiceEmailPassword,userName:userName,userPassword:userPassword,customer:customer,profileCalculations:profileCalculations };
        try {
            const companyPromise = await axios.put(CompaniesURL + "UpdateCompany", updateCompany);
            await dispatch(UpdateCompany(companyPromise.data));
            const response = await companyPromise;
            return companyPromise.data;
        }
        catch (e) {
            console.log(e);
        }
    }
