import React, { useEffect } from 'react';
import { MDBBtn, MDBCard, MDBCardBody, MDBCardImage, MDBCardTitle, MDBCardText, MDBCol } from 'mdbreact';
import { useDispatch } from 'react-redux';
import {  getByCategorie} from '../Redux/Product/ProductsThunk'
import { useParams } from 'react-router';
import { useState } from 'react';

const  ShowProductsByCategory =(code) => {
   const params=useParams;
  const dispatch=useDispatch();
   let c=code.match.params.id;
   let [p,setP]=useState([]);

   useEffect(async () => {
   let p1=await getByCategorie(dispatch,c); 
    setP(p1);
  }, [])
 
    
 
  return (
    <h2>מוצרים לפי קטגוריה</h2>
  )
}

export default ShowProductsByCategory;
const pStyles = {
  float:"right",
      margin:"auto",
      with:"100px",
      margin:'20px 3%'
      
  }
