import { useEffect } from "react";
import { getAllCustomersFromServer,getCustomerFromserverById,addCustomer } from '../Redux/Customer/CustomersThunk'
import { useState } from 'react';
import { useDispatch } from 'react-redux';

export default function ShowCustomers() {
  let myCustomersFromServer
  let myCustomer,myAddCustomer
  const [customersList,setcustomersList]=useState ([])
  const [oneCustomer,setoneCustomer] = useState({})
  const dispatch = useDispatch();

  useEffect(async () => {
    myCustomersFromServer = await getAllCustomersFromServer(dispatch);
  
    await console.log(myCustomersFromServer)
    await setcustomersList(myCustomersFromServer)

    myCustomer = await getCustomerFromserverById(dispatch,1,"611611");
    await console.log(myCustomer)
      await setoneCustomer(myCustomer)

      myAddCustomer = await addCustomer(dispatch,5,"sh","711711","322461309");   
            await console.log("הוספת לקוח  "+myAddCustomer);
            await setcustomersList(myAddCustomer);

      }, [])
     return <>
       {<h1>Show customers to manager</h1> }
       { myCustomersFromServer!=undefined ?
     myCustomersFromServer.foreach(function(item) {

     
        <MDBRow>
        <MDBCol md='4'>
          <MDBCard>
            <MDBCardImage
              top
              src='https://mdbcdn.b-cdn.net/img/Photos/Horizontal/Work/4-col/img%20%2821%29.jpg'
              overlay='white-slight'
              hover
              waves
              alt='MDBCard image cap'
            />
            <MDBCardBody className='elegant-color white-text rounded-bottom'>
              <a href='#!' className='activator waves-effect waves-light mr-4'>
                <MDBIcon icon='share-alt' className='white-text' />
              </a>
              <MDBCardTitle>{item.nameCustomer}</MDBCardTitle>
              <hr className='hr-light' />
              <MDBCardText className='white-text'>
                Some quick example text to build on the card title and make up the
                bulk of the card&apos;s content.
              </MDBCardText>
              <a href='#!' className='black-text d-flex justify-content-end'>
                <h5 className='white-text'>
                  Read more
                  <MDBIcon icon='angle-double-right' className='ml-2' />
                </h5>
              </a>
            </MDBCardBody>
          </MDBCard>
        </MDBCol>
      </MDBRow>
    })
  :<div>אין מוצרים</div>}
  </>
}
